import React, {useState, useEffect} from 'react';
import { Link, Route } from 'react-router-dom';
import {Nav, Tab, Row, Col, Form, TabContainer} from 'react-bootstrap'
import styled from 'styled-components';
import { motion} from "framer-motion";
import axios from 'axios'

//images
import redCar from '../assets/moto.jpg'
import service from '../assets/service.png'
import logo_transparent from '../assets/logo-transparent.png'

//images
import dark from '../assets/dark1.jpg'

//icons
import {AiFillCaretRight} from 'react-icons/ai'

//component
//import Product_Carousel from '../components/Carousel'
import Recommandation from '../components/Recommandation'
import Coordonnee from '../components/Coordonnee'
import Navbar from '../components/Navbar'


const Styles = styled.div`
    height:100%;
    display: grid;
    grid-template-columns: 1fr repeat(12, minmax(auto, 4.2rem)) 1fr;
    grid-template-rows:5.8rem 28.5rem 5rem auto;
    gap : 0 2rem;

    .navbar-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 2;
        z-index:10;
    }
    .promo-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 4;
        display:flex;
        flex-direction:row;
        justify-content:center;
        align-items:center;
    }
    .promo-left-side{
         display:flex;
         width: 40%;
         height: 100%;
         background:url(${dark});
         background-size:cover;
         align-items:center;
         justify-content:flex-end;
    }
    .info-promo{
       width:100%;
       height: 100%;
       display:flex;
       flex-direction:column;
       justify-content:center;
       align-items:center;
       color: white;
    }
    .inside-info-promo{
       width:75%;
       height: 60%;
       display:flex;
       flex-direction:column;
       justify-content:center;
       align-items:flex-start;
       color: white;
    }
    .promo-right-side{
        display:flex;
        width: 60%;
