import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import {Nav, Navbar, Tab, Row, Col, Form} from 'react-bootstrap'
import styled from 'styled-components';
import { motion} from "framer-motion";
import axios from 'axios'

//images
import redCar from '../assets/red-car.jpg'
import pneus from '../assets/pneus.png'
import commande from '../assets/commandes.png'
import payment from '../assets/payement.png'
import service from '../assets/service.png'
import logo_transparent from '../assets/logo-transparent.png'
import remise from '../assets/remise-01.png'

//logo
import logo from '../assets/logo-rouge-blanc.png'

//icons
import {AiFillCaretRight} from 'react-icons/ai'



const Styles = styled.div`
height: 150vh;
display: grid;
  grid-template: repeat(16, [row] 1fr) / repeat(14, [col] 1fr);
margin-bottom:5%;
  .item-1 {
  grid-column: col 1 / span 14;
  grid-row: row 1 / span 2 ;
  background: transparent;
  z-index:1;
}


.item-3 {
  grid-column: col 1 / span 5;
  grid-row: row 1 / span 11 ;
  background: #2F3945;
  opacity: 0.8;
  display:flex;
  flex-direction:row;
  justify-content:flex-end;
  align-items: center;
}

.item-4 {
  grid-column: col 6 / span 11;
  grid-row: row 1 / span 11 ;
  background: -webkit-linear-gradient( rgba(0, 0, 0, 0.3) 100%,transparent 100%), url(${redCar});
  background-size:cover;
  display:flex;
  flex-direction:row;
  justify-content:flex-start;
  align-items:center;
}

.item-5 {
  grid-column: col 2 / span 12;
  grid-row: row 9 / span 4;
  background: linear-gradient(90.84deg, #EF1A23 0.61%, #FB3C29 99.42%);  
  opacity: 0.8;
  color:white;
  z-index: 1;
}
.search-first-row{
    display:flex;
    flex-direction:row;
    padding:1%;
    height:50%;
    justify-content:center;
    align-items:center;
}
.search-second-row{
    display:flex;
    flex-direction:row;
    justify-content:flex-end;
    align-items:flex-end;
    height:50%;
}
.search-button{
    font-size:1.5em;
    font-weight:600;
    background:#2F3945;
    border-radius:5px;
    border:none;
    color:white;
    margin: 0 3% 0 0;
}
.search-input{
    margin:1% 1%;
    width: 12%;
}

.navbar-links{
    color:white;
    font-size:1.5em;
    font-family: 'Teko', sans-serif; 
    &:hover{
        border-bottom: solid rgba(255, 0, 0, 0.5);
        text-decoration:none;
    }
}
.item-6 {
  grid-column: col 1 / span 14;
  grid-row: row 12 / span 4 ;
  background: linear-gradient(360deg, #FFFFFF 0%, rgba(255, 255, 255, 0.08) 57.24%, rgba(47, 57, 69, 0.54) 104.71%);
}
.item-7{
  grid-column: col 2 / span 12;
  grid-row: row 14 / span 4;
  background: transparent;
}
.item-8{
  grid-column: col 2 / span 12;
  grid-row: row 3 / span 5;
  background: url(${remise}) no-repeat;
  z-index:1;
}
.coordonnee{
    display:flex;
    flex-direction:row;
    align-items:center;
    justify-content:center;
    width:100%;
    height:100%;
    padding: 2% 0;
}
.card-coordonnee{
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:center;
    width:35%;
    height:100%;
    border-radius:10px;
}
.card-col-1{
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:center;
    width:100%;
    height:100%;
    background:transparent;
    border-radius:50%
}
.card-col-2{
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:center;
    width:100%;
    height:100%;
}
.card-col-3{
    display:flex;
    flex-direction:column;
    align-items:flex-start;
    justify-content:center;
    width:5%;
    height:100%;
    padding-left:5%;
}
`;

function Header(props){
    const [key, setKey] = useState("dimension");
    const ActiveStyle = {
        textAlign: "left",
        background: "transparent",
        color: "white",
        fontSize:'1.5em',
        fontFamily:'Teko, sans-serif ' 
    };
    const inActiveStyle = {
        ...ActiveStyle,
        background: "rgba(0, 0, 0, 0.4)",
        color: "white",
        fontSize:'1.5em',
        fontFamily:'Teko, sans-serif ' 

    };


