import React, {useEffect, useState} from "react";
import { Link } from 'react-router-dom';
import { MDBContainer, MDBFooter } from "mdbreact";
import styled from 'styled-components';

//icons 
import { FaFacebookF, FaInstagram, FaLinkedin, FaPhone, FaTwitter } from "react-icons/fa";
import { MdMail } from "react-icons/md";
import { IoIosSend } from "react-icons/io";

//logo
import logo from '../assets/PneuS-06.png'


const Styles = styled.div`
   .font-small{
     background: #555;
     color:white;
   }
   .footer-copyright{
     color : white;
     border-top : solid;
     margin-top: 2%;
   }
   .footer-row{
     display:flex;
     flex-direction:row;
     padding: 0 2%;
     @media only screen and (max-width: 790px) {
       flex-direction:column;
       justify-content:center;
     }
     
   }
   .footer-column{
     display:flex;
     flex-direction:column;
     justify-content:flex-start;
     align-items:flex-start;
     font-size:18px;
     width:33%;
     @media only screen and (max-width: 790px) {
           font-size:20px;
           width:100%;
           align-items:center;
           margin-bottom:10%;
     }
     @media only screen and (max-width: 450px) {
           font-size:18px
     }
   }
   .title{
     font-size:1.5em;
     font-weight:600;
   }

   .list-unstyled, a {
     color:white;
     font-size:0.9em;
     text-decoration:none;
     &:hover{
       color:#cd4339;
     }
   }
   .icon-section{
    width:100%;
    display:flex; 
    flex-direction:row;
    @media only screen and (max-width: 790px) {
       justify-content:center;
     }
   }
   .Style-icon{
      width:1.2em;
      height:1.2em;
      margin-right:2%;
      &:hover{
        color:#cd4339;
      }
      @media only screen and (max-width: 450px) {
        width:1em;
      height:1em;
     }
   }
   .description{
     font-size:0.8em;
     @media only screen and (max-width: 790px) {
           text-align:center;
     }
   }

   .contact-input{
     background: #333;
     color:white;
     border:none;
     border-bottom: solid white;
     width: 100%;
     margin-bottom:2%;
     font-size:0.8em;
   }
   .bouton-contact{
     background:#4d8abc;
     color:whitesmoke;
     border:none;
     font-size:0.8em;
     font-weight:700;
     width:30%;
     height:12%;
     border-radius:5px;
     &:hover{
       color:#cd4339;
     }
   }
`;


const Footer = () => {
  
  return (
    <Styles>
    <MDBFooter color="blue" className="font-small pt-4 ">
        <div className='footer-row'>
          <div className='footer-column'>
            <img
              src={logo}
              width="140"
              height="65"
              className="d-inline-block align-top"
              alt="Pneuservice.dz"
            />
              <p className="list-unstyled">
              Les plus grandes marques disponibles
              </p>
              <p className="list-unstyled">
              Des centres de montage agrées 
              </p>
              <p className="list-unstyled">
              Des milliers de références 
              </p>
              <p className="list-unstyled">
              Les meilleurs prix du marché
              </p>
              <p className="list-unstyled">
              Du temps gagné dans votre recherche
              </p>
            
          </div>
          <div className='footer-column' style={{paddingLeft:'5%'}}>
            <p className="title">Changer vos pneu plus facilement</p>
              <p className="list-unstyled">
              1. Recherchez la dimension de votre pneu
              </p>
              <p className="list-unstyled">
              2. choisissez la marque et le nombre de pneu 
              </p>
              <p className="list-unstyled">
              3. faites votre commande.
              </p>
              <p className="list-unstyled">
              4. Payez à la livraison ou au centre de montage agrée.
              </p>
              <p className="list-unstyled">
              La dimension recherchée n’est pas disponible !? <br/> demandez un devis via notre formulaire. 
              </p>
          </div>
          <div className='footer-column'>
            <p className="title">Nous Contacter</p>
            <p>Pour toutes informations complémentaires, veuillez contacter notre service commercial</p>
            <p className='description'><FaPhone style={{ height: '40px', width:'40px'}}/> 0560 66 99 99</p>
            <p className='description'><MdMail style={{ height: '40px', width:'40px'}}/> Yafour.nabil@yahoo.fr</p>
            <p className='icon-section'>
              <FaFacebookF className='Style-icon'/>
              <FaInstagram className='Style-icon' />
              <FaLinkedin className='Style-icon'/>
              <FaTwitter className='Style-icon'/>
            </p>
          </div>
        </div>
        <div>

        </div>
      <div className="footer-copyright text-center py-3">
        <MDBContainer fluid>
          &copy; {new Date().getFullYear()} Copyright: <a href="https://www.monsterstudio.org/"> Monster studio </a>
        </MDBContainer>
      </div>
    </MDBFooter>
    </Styles>
  );
}

export default Footer;
