import React, {useState, useEffect} from 'react';
import styled from 'styled-components';

//icons
import {FaCloudShowersHeavy, FaGasPump, FaVolumeUp} from 'react-icons/fa'

//images
import default_img from '../assets/default_image.png'


const Styles = styled.div`
   padding : 0 8%;
  

   .resultat{
    display:flex;
    flex-direction:row;
    justify-content:center;
    align-items : center;
   }
   
   .Left-side{
     display:flex;
     flex-direction:column;
     justify-content:center;
     align-items:center;
     width:80%;
   }

   .Right-side{
     display:flex;
     flex-direction:column;
     justify-content:center;
     align-items:center;
     background:#555;
     width:20%;
     height:auto;
     padding:2%;
   }

   .sidebar-content{
       
   }

   .sticky {
    position: fixed;
    top: 0;
   }
`;

const Card_pneu = styled.div`
    scroll-behavior: smooth;
    display:flex;
    flex-direction: row;
    align-items:center;
    box-shadow:5px 10px 20px 1px rgba(0,0,0, 0.253);
    border-radius:10px;
    margin: 1% 0;
    height:180px;
    width:100%;

    .card-img{
        display:flex;
        flex-direction:column;
        width:15%;
    }
    .default-img{
        position: relative;
        height:100%;
        width: 100%;
        color:#333;
    }
    .card-info-1{
    }
    .card-info-2{
        display:flex;
        flex-direction:row;
    }
    .card-info-3{
        font-size:2em;
        color:#333;
    }
    .overlay {
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        height: 100%;
        width: 100%;
        opacity: 0.7;
        transition: .5s ease;
        background-color: #fff;
    }
    .overlay-1{
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        height: 100%;
        width: 100%;
        transition: .5s ease;
    }
    .text {
        color: #000;
        font-size: 1em;
        font-weight:600;
        position: absolute;
        top: 50%;
        left: 50%;
        -webkit-transform: translate(-50%, -50%);
        -ms-transform: translate(-50%, -50%);
        transform: translate(-50%, -50%);
        text-align: center;
    }
`;

function SearchResult(props){
    window.onscroll = function() {myFunction()};
    function myFunction() {
    var sidebar = document.getElementById("mySidebar");
    var sticky = sidebar.offsetTop;

    if (window.pageYOffset > sticky) {
        sidebar.classList.add("sticky");
    } else {
        sidebar.classList.remove("sticky");
    }
    }
    return(
        <React.Fragment>
            <Styles>
                <h5 style={{fontWeight:'600', color:'#444'}}>Notre selection de pneus : {props.dimensions.largeur}/{props.dimensions.hauteur} R{props.dimensions.diametre} {props.dimensions.charge} {props.dimensions.vitesse}</h5>
                <hr/>
                <div className='resultat'>
                <div className=' Right-side' id='mySidebar'>
                    <div className='sidebar-content'>
                        <p>LoremVoluptate occaecat nisi ipsum in ullamco qui. Velit eiusmod deserunt aliquip deserunt. Labore ad minim eiusmod mollit cillum occaecat non veniam non velit. Fugiat occaecat excepteur nulla magna velit duis eu ut.</p>
                    </div>
                </div>
                <div className='Left-side'>
                    {props.resultat.map( (res) => 
                    <Card_pneu>
                        <div className='card-img'>
                            {res.image_url === null ? 
                            <div className='default-img'>
                            <img
                                src={`${default_img }`}
                                width='130'
                                height='180'
                                className='image'
                            />
                            <div className="overlay">
                                    {res.marque_img === null ? null :
                                        <img
                                            src={`https://www.monsterstudio.org${res.marque_img}`}
                                            width='150'
                                            height='30'
                                        />
                                    }
                                    <div className="text">Image Non Disponible</div>
                                </div>
                            </div>
                            :
                            <div className='default-img'>
                                <img
                                    src={`https://www.monsterstudio.org${res.image_url}`}
                                    width='130'
                                    height='180'
                                    className='image'
                                />
                                <div className="overlay-1">
                                    {res.marque_img === null ? null :
                                        <img
                                            src={`https://www.monsterstudio.org${res.marque_img}`}
                                            width='150'
                                            height='30'
                                        />
                                    }
                                </div>
                            </div> 
                            }
                        </div>
                        <div className='card-info-1'>
                        <p style={{fontSize :'1.3em', fontWeight:'700', color:'#444', margin:'0', padding:'0'}}>{res.collection}</p>
                        <p style={{fontSize :'1em', fontWeight:'600', color:'#444',margin:'0', padding:'0' }}>{props.dimensions.largeur}/{props.dimensions.hauteur} R{props.dimensions.diametre} {props.dimensions.charge} {res.vitesse}</p>
                        <p style={{fontSize :'1em', fontWeight:'600', color:'#444', margin:'0', padding:'0' }}>categorie : {res.type}</p>
                        <div className='card-info-2'>
                            <FaGasPump style={{fontSize :'1.8em', color:'#777', marginRight:'5%' }}/>
                            <p>{res.carburant}</p>
                            <FaCloudShowersHeavy style={{fontSize :'1.8em', color:'#777', marginRight:'5%' }}/>
                            <p>{res.adherence}</p>
                            <FaVolumeUp style={{fontSize :'1.8em', color:'#777', marginRight:'5%' }}/>
                                <p>{res.bruit}</p>
                        </div>
                        </div>
                        <div className = 'card-info-3'>
                            <p>{res.price}</p>
                        </div>
                    </Card_pneu>
                    )}            
                </div>
                </div>
        </Styles>
        </React.Fragment>
    )
}

export default SearchResult;

/**
