import React, {useState} from 'react';
import { Link, useHistory } from 'react-router-dom';
import {Nav, Navbar, Tabs, Tab} from 'react-bootstrap'
import styled from 'styled-components';
import { useSpring, animated } from 'react-spring'

import backgroundAuto from '../assets/photo-of-red-suv-on-dirt-road.jpg'
import backgroundMoto from '../assets/red-and-white-dirt-bike.jpg'
import backgroundAgro from '../assets/toyota-4x4-sunset.jpg'
import backgroundPromo from '../assets/silver-car-with-dust.jpg'

import logo from '../assets/PneuS-03.png'

//icons
import {GiPhone} from 'react-icons/gi'
import {MdMail} from 'react-icons/md'


const Styles = styled.div`
font-size:16px;
.navbar-links{
    color:white;
    font-weight:400;
    &:hover{
        text-decoration:none;
    }
}
.promo-link{
    background: rgba(255, 0, 0, 0.5);
    border-radius:5px;
    font-weight:900
}
a.nav-link{
    &:hover{
        border-bottom: solid rgba(255, 0, 0, 0.5);
        text-decoration:none;
    }
}

.landing-text{
    color: white;
    width:90vw;
    height:50%;
    display:flex;
    flex-direction:row;
    justify-content:start;
    align-items:center;
    padding: 10% 15% ;
    margin-bottom: 5%;
}
.landing-page-left-row{
    display:flex;
    flex-direction:column;
    justify-content:center;
}
.landing-big-title{
   padding:0;
   margin:0;
   display:flex;
   flex-direction:column;
}

.landing-button{
    font-size:1.5em;
    font-weight:600;
    background: #EF1A23;
    height:20%;
    width:50%;
    border-radius:5px;
    border:none;
    color:white;
}
.search-bar{
    height:auto;
    display:flex;
    flex-direction:row;
    flex : 1 1 auto;
}
.search-bar-first-col{
   background: rgba(255, 0, 0, 0.4);
   width:80%;
   height:100%;
   color:white;
}
.search-bar-second-col{
    width:20%;
    height:auto;
    background: rgba(0, 0, 0, 0.6)
}
.nav-tabs > a
 {
    font-weight:bolder;
    color:white;
}
`;

function Header(props) {  
    const [isShown, setIsShown] = useState(false)

    //background picker
    function backgroundSelect(){
        switch(props.navigation){
            case '/PneuMoto': return backgroundMoto;
            case '/PneuAgro': return backgroundAgro;
            case '/promos': return backgroundPromo;
            default: return backgroundAuto
        }
    }
    return(
       <React.Fragment>
           <Styles 
              style={{
                  background:`-webkit-linear-gradient( rgba(0, 0, 0, 0.5) 100%,transparent 100%), url(${backgroundSelect()})`, 
                  height:'100vh',
                  backgroundSize:'cover',
                  backgroundPosition:'center'
                  }}>
               
                <Navbar expand="lg" bg="none" variant="dark">
                    <Navbar.Brand>
                    <img
                        src={logo}
                        width="120"
                        height="50"
                        className="d-inline-block align-top"
                        alt="React Bootstrap logo"
                    />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link><Link className='navbar-links' to='/'>Pneu Auto</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/PneuMoto'>Pneu Moto</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Pneu Genie civile</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Pneu Manutation</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/PneuAgro'>Pneu Agro</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Pneu Poids lourd</Link></Nav.Link>
                        </Nav>
                        <Nav className="ml-auto">
                            <Nav.Link className='promo-link'><Link className='navbar-links' to='/promos'>Promo</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Montage</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Marques</Link></Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
                <div className='landing-text'>
                    <div className='landing-page-left-row'>
                        <div className='landing-big-title'>
                           <h1 style={{fontWeight:'900', fontSize:'8em'}}>-40% </h1>
                           <p style={{fontWeight:'900', fontSize:'2em', margin:'0'}}>sur tous nos produits</p>
                        </div>
                        <p style={{fontWeight:'600', fontSize:'1em', margin:'0'}}>C'est maintenant ou jamais ne ratez pas l'occasion <br/> Profiter de cette offre speciale</p> 
                        <p style={{fontWeight:'600', fontSize:'2em', margin:'0'}}>Jusqu'au 20 Juin</p>
                        <button className='landing-button'>J'EN PROFITE</button>
                    </div>
                </div>
                <div className='search-bar'>
                    <div className='search-bar-first-col'>
                       <Tabs className='tabs-style' fill  id="uncontrolled-tab-example">
                            <Tab className='tabs-content' eventKey="dimension" title="Recherche Par dimension">
                                ca c'est rechrche par dimension
                            </Tab>
                            <Tab className='tabs-content' eventKey="profile" title="Recherche Par vehicule">
                                ca c'est recherche par vehicule
                            </Tab>
                        </Tabs>
                    </div>
                    <div className='search-bar-second-col'>
                        something
                    </div>
                </div>
           </Styles>
       </React.Fragment> 
    )}

export default Header;

/**
 * <div className='contact-navbar'>
                   <GiPhone 
                     className='contact-icon'
                     onMouseEnter={() => {setIsShown(true); console.log(isShown) }}
                     onMouseLeave={() => {setIsShown(false); console.log(isShown) }}
                     />
                    <MdMail className='contact-icon'/>
               </div>


               import backgroundMoto from '../assets/photo-of-red-suv-on-dirt-road.jpg'
import backgroundAgro from '../assets/photo-of-red-suv-on-dirt-road.jpg'
import backgroundPdsLourd from '../assets/photo-of-red-suv-on-dirt-road.jpg'
import backgroundPdsLourd from '../assets/photo-of-red-suv-on-dirt-road.jpg'
import backgroundPdsLourd from '../assets/photo-of-red-suv-on-dirt-road.jpg'
 */
