import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import {Nav, Tab, Row, Col, Form, TabContainer} from 'react-bootstrap'
import styled from 'styled-components';
import { motion} from "framer-motion";
import axios from 'axios'

//images
import redCar from '../assets/red-car.jpg'
import pneus from '../assets/pneus.png'
import commande from '../assets/commandes.png'
import payment from '../assets/payement.png'
import service from '../assets/service.png'
import logo_transparent from '../assets/logo-transparent.png'
import remise from '../assets/remise-01.png'



//icons
import {AiFillCaretRight} from 'react-icons/ai'
//component
import Product_Carousel from '../components/Carousel'
import Recommandation from '../components/Recommandation'
import Header from '../components/Header'
import Navbar from '../components/Navbar'


const Styles = styled.div`
    height:100%;
    display: grid;
    grid-template-columns: 1fr repeat(12, minmax(auto, 4.2rem)) 1fr;
    grid-template-rows:5.8rem 28.5rem 5rem auto;
    gap : 0 2rem;

    .navbar-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 2;
    }
    .promo-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 4;
        display:flex;
        flex-direction:row;
        justify-content:center;
        align-items:center;
    }
    .promo-left-side{
         display:flex;
         width: 40%;
         height: 100%;
         background:#343a40;
         align-items:center;
         justify-content:flex-end;
    }
    .promo-right-side{
        display:flex;
        width: 60%;
        height: 100%;
        background: -webkit-linear-gradient( rgba(0, 0, 0, 0.3) 100%,transparent 100%), url(${redCar});
        background-size:cover;
        align-items:center;
        justify-content:flex-start;
    }
    .content-item{
        grid-column: 2 / span 12;
        grid-row: 3 / 5;
        background:#fff;
        z-index:10;
        text-align:center;

    }
    .search-item{
        height:auto;
        width:100%;
        background: linear-gradient(90.84deg, #EF1A23 0.61%, #FB3C29 99.42%);  
        color:white;
        padding-bottom:1%;
    }
    .search-first-row{
        display:flex;
        flex-direction:row;
        padding:1%;
        height:50%;
        justify-content:center;
        align-items:center;
    }
    .search-second-row{
        display:flex;
        flex-direction:row;
        justify-content:flex-end;
        align-items:flex-end;
        height:50%;
    }
    .search-button{
        width:14%;
        font-size:1.5em;
        font-weight:600;
        background:#2F3945;
        border-radius:5px;
        border:none;
        color:white;
        margin:0 2% 1% 0;
    }
    .search-input{
        margin:1% 1%;
        width: 15%;
    }
    .coordonnee{
        display:flex;
        flex-direction:row;
        align-items:center;
        justify-content:center;
        width:100%;
        height:100%;
        padding: 2% 0;
    }
    .card-coordonnee{
        display:flex;
        flex-direction:column;
        align-items:center;
        justify-content:center;
        width:35%;
        height:100%;
        border-radius:10px;
    }
    .card-col-1{
        display:flex;
        flex-direction:column;
        align-items:center;
        justify-content:center;
        width:100%;
        height:100%;
        background:transparent;
        border-radius:50%
    }
    .card-col-2{
        display:flex;
        flex-direction:column;
        align-items:center;
        justify-content:center;
        width:100%;
        height:100%;
    }
    .card-col-3{
        display:flex;
        flex-direction:column;
        align-items:flex-start;
        justify-content:center;
        width:5%;
        height:100%;
        padding-left:5%;
    }
    .marques{
        padding:0;
        margin: 5% 0;
    }
    .titre-marques{
        font-size:2em;
        font-weight:600;
        color:#555;
        font-family: 'Teko', sans-serif;
    }

`;

function Home(props){

    //search styling
    const [key, setKey] = useState("dimension");
    const ActiveStyle = {
        textAlign: "left",
        background: "transparent",
        color: "white",
        fontSize:'1.5em',
        fontFamily:'Teko, sans-serif ' 
    };
    const inActiveStyle = {
        ...ActiveStyle,
        background: "rgba(0, 0, 0, 0.4)",
        color: "white",
        fontSize:'1.5em',
        fontFamily:'Teko, sans-serif ' 

    };

    //data management
    const [formDataDimension, setFormDataDimension] = useState({
        largeur :'--', 
        hauteur :'--',
        diametre : '--',
        charge :'--', 
        vitesse:'Tous',
        marque :'Tous'
    })
    const [formDataVehicule, setFormDataVehicule] = useState({
        marque :'--', 
        modele :'--',
        motorisation : '--',
        annee :'--', 
        taille:'-- --'
    })

    //les dimensions 'dimension'
    const [largeurs, setLargeurs]= useState([]);
    const [hauteurs, setHauteurs]= useState([]);
    const [diametres, setDiametres]= useState([]);
    const [charges, setCharges]= useState([]);
    const [vitesses, setVitesses] = useState([]);
    const [marques, setMarques]= useState([]);
    
    //les dimensions vehicule
    const [marqueVs, setMarqueVs] = useState([]);
    const [modeles, setModeles] = useState([]);
    const [motorisations, setMotorisations] = useState([]);
    const [annees, setAnnees] = useState([]);
    const [tailles, setTailles] = useState([]);

    //
    const {largeur, hauteur, diametre, charge, vitesse, marque} = formDataDimension
    const {marqueV, modele, motorisation, annee, taille} = formDataVehicule

    //handle change from inputs
    const handleChangeDimension = text => e => {
        setFormDataDimension({...formDataDimension, [text]: e.target.value})
        console.log(formDataDimension)
    }

    const handleChangeVehicule = text => e => {
        setFormDataVehicule({...formDataVehicule, [text]: e.target.value})
    }

    //recherche par dimension
    //get all Largeurs, vitesses et marques
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/dimension/largeur`)
        .then(res => {
            setLargeurs(res.data)  
        })
        .catch(err => {
            console.log(err)
        })
        axios.get(`${process.env.REACT_APP_API_URL}/search/dimension/vitesse`)
        .then(res => {
            setVitesses(res.data.sort())  
        })
        .catch(err => {
            console.log(err)
        })
        axios.get(`${process.env.REACT_APP_API_URL}/search/dimension/marque`)
        .then(res => {
            setMarques(res.data.sort())  
        })
        .catch(err => {
            console.log(err)
        })
    }, [])
    /*
    //get hauteur depending on largeur
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/dimension/hauteur`, {
            largeur
        })
        .then(res => {
            setHauteurs(res.data.sort())  
        })
        .catch(err => {
            console.log(err)
        })
    },[largeur])

    //get diametre depending on largeur et hauteur
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/dimension/diametre`, {
            largeur, hauteur
        })
        .then(res => {
            setDiametres(res.data.sort())  
        })
        .catch(err => {
            console.log(err)
        })
    },[hauteur])

    //get charge depending on largeur et hauteur et diametre
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/dimension/charge`)
        .then(res => {
            setCharges(res.data.sort())  
        })
        .catch(err => {
            console.log(err)
        })
    },[diametre])


    //recherche par vehicule
    //get all marques
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/vehicule/marque`)
        .then(res => {
            setMarqueVs(res.data.sort());
        })
        .catch(err => {
            console.log(err)
        })
    }, []);

    //get all modeles depending on marques
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/vehicule/modele`)
        .then(res => {
            setModeles(res.data.sort());
        })
        .catch(err => {
            console.log(err)
        })
    }, [marque]);

    //get all motorisations depending on marques et modele
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/vehicule/motorisation`)
        .then(res => {
            setMotorisations(res.data.sort());
        })
        .catch(err => {
            console.log(err)
        })
    }, [modele]);

    //get all annee depending on marque et modele et motorisation
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/vehicule/annee`)
        .then(res => {
            setAnnees(res.data.sort());
        })
        .catch(err => {
            console.log(err)
        })
    }, [motorisation]);

    //get all tailles depending on marque et modele et motorisation et annee
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/vehicule/taille`)
        .then(res => {
            setTailles(res.data.sort());
        })
        .catch(err => {
            console.log(err)
        })
    }, [annee]);

    //get largeur, hauteur, diametre, charge, vitesse
    useEffect(() => {
        axios.get(`${process.env.REACT_APP_API_URL}/search/vehicule/params`,{
            marque, modele, motorisation, annee, taille
        })
        .then(res => {
            setFormDataDimension({
                ...formDataDimension,
                largeur :res.data.largeur, 
                hauteur :res.data.hauteur,
                diametre :res.data.diametre,
                charge :res.data.charge
            })
        })
        .catch(err => {
            console.log(err)
        })
    }, [taille]);

    //submit data to backend
    /*const handleSubmit = e => {
    e.preventDefault()
    axios.post(`${process.env.REACT_APP_API_URL}/search/pneus`, {
        largeur, hauteur, diametre, charge, vitesse, marque
         }).then(res => {
           console.log(largeur+' '+hauteur+' '+diametre+' '+charge+' '+vitesse+' '+marque)
           console.log(res.data)
           
         }).catch(err => {
          console.log(err)
         })
    }*/

    return(
        <React.Fragment>
            <Styles>
                <div className='navbar-item'>
                    <Navbar/>
                </div>
                <div className='promo-item'>
                    <div className='promo-left-side'>
                        <img
                            src={logo_transparent}
                            width='80'
                            height='170'
                            style={{margin:'0 0 50% 0', padding:'0'}}
                        />
                    </div>
                    <div className='promo-right-side'>
                        <img
                            src={service}
                            width='20'
                            height='150'
                            style={{margin:'0 0 28% 0', padding:'0'}}
                        />
                    </div>
                </div>
                <div className='content-item'>
                    <div className='search-item'>
                        <Tab.Container id="controlled-tab-example" activeKey={key} onSelect={key => setKey(key)}>
                            <Row>
                                <Col>
                                    <Nav.Link  
                                    eventKey="dimension" 
                                    style={key === "dimension" ? ActiveStyle : inActiveStyle}
                                    >
                                        <AiFillCaretRight style={{marginRight:'1%'}}/>
                                        Recherche Par dimension
                                    </Nav.Link>
                                </Col>
                                <Col>
                                    <Nav.Link  
                                    eventKey="vehicule" 
                                    style={key === "vehicule" ? ActiveStyle : inActiveStyle}
                                    >
