import React, {useState, useEffect} from 'react';
import {useRouteMatch} from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios'

//components
import Navbar from '../components/Navbar'

//images
import default_img from '../assets/default_image.png'

//image vente budjet 
import pneu1_vente_budjet from '../assets/vente_budjet/CONTI_PREMIUM_CONTACT_6_Continental.png'
import pneu2_vente_budjet from '../assets/vente_budjet/CROSSCLIMATEHAXX_Michelin.png'
import pneu3_vente_budjet from '../assets/vente_budjet/DUELER_H_T_684_II_Bridgistone.png'
import pneu4_vente_budjet from '../assets/vente_budjet/PILOT_SPORT_4_Michelin.png'
import pneu5_vente_budjet from '../assets/vente_budjet/SCORPION_VERDE_ALL_SEASON_Pireli.png'

const Styles = styled.div`
/*------------details container---------------*/
.details-container{
    display:flex;
    flex-direction:row;
    justify-content:center;
    align-items:center;
    padding:5%;
    height:100vh;
    width:100vw;
}
/**________________________________________________ */
.image-container{
    display:flex;
    flex-direction:column;
    width:30%;
    height:100%;
    background:#333;
}

.active-image-container{
    display:flex;
    flex-direction:row;
    width:100%;
    height:80%;
}

.details-icons{
    display:flex;
    flex-direction:column;
    width:10%;
    height:80%;
}

.image-pneu-container{
    width:90%;
    height:80%;
}

.image-pneu{
    
    height:100%;
}

.default-img{
    display:flex;
    justify-content: flex-end;
    position: relative;
    height:100%;
    width: 100%;
    color:#333;
}

.text {
    color: #000;
    font-size: 1.2em;
    font-weight:600;
}

.overlay {
    display:flex;
    justify-content:flex-end;
    align-items:center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    height: 100%;
    width: 100%;
    opacity: 0.7;
    transition: .5s ease;
    background-color: #fff;
    padding-right:10%;
}

.image-marque-container{
    width:100%;
    display:flex;
    justify-content: flex-end;
}

.image-marque{
    width:50%;
    height:20%;
}


.image-marque-container{

}

.image-marque{

}

.images-slider{
    display:flex;
    flex-direction:row;
    width:100%;
    height:20%;
    background:#ccc;
}
/**_____________________________________________ */
.infos-container{
    width:40%;
    height:100%;
    background:#777
}
/*______________________________________________*/
.commande-container{
    width:20%;
    height:100%;
    background:#999
}

/*------------fin details container-----------*/
/*------------similar products---------------*/
.similar-products{
    display:flex;
    flex-direction:row;
    justify-content:center;
    align-items:center;
}
/*------------fin similar products------------*/
`;

function Detail(){
    let match = useRouteMatch();
    const [pneu, setPneu] = useState([])

    const vente_budjet_content =[
        {
            image : pneu1_vente_budjet,
            name : 'CONTI PREMIUM CONTACT 6',
            marque : 'Continental',
            prix : '2560.00 Dz'
        },
        {
            image : pneu2_vente_budjet,
            name : 'CROSSCLIMATEHAXX',
            marque : 'Michelin',
            prix : '2560.00 Dz'
        },
        {
            image : pneu3_vente_budjet,
            name : 'DUELER H T 684 II',
            marque : 'Continental',
            prix : '2560.00 Dz'
        },
        {
            image : pneu4_vente_budjet,
            name : 'PILOT SPORT',
            marque : 'Michelin',
            prix : '2560.00 Dz'
        },
        {
            image : pneu5_vente_budjet,
            name : 'SCORPION VERDE ALL SEASON',
            marque : 'Pireli',
            prix : '2560.00 Dz'
        }
    ]
    
    useEffect(() => {
        var id = match.params.id_pneu
        axios.post(`${process.env.REACT_APP_API_URL}/pneu/details`, {
            id
        })
        .then(res => {
            console.log(res.data[0])
           setPneu(res.data[0])
        })
        .catch(err => {
            console.log(err)
        })
    }, []);
    return(
        <React.Fragment>
            <Navbar bg="dark" variant="dark"/>
            <Styles>
                <div className='details-container'>
                    <div className='image-container'>
                        <div className='active-image-container'>
                            <div className='details-icons'></div>
                            <div className='image-pneu-container'>
                            {pneu.image_url === null ? 
                                <div className='default-img'>
                                    <img
                                        src={`${default_img }`}
                                        className='image-pneu'
                                    />
                                    <div className="overlay">
                                        <div className="text">Image Non Disponible</div>
                                    </div>
                                </div>
                             :
                                <img
                                    src={`https://www.monsterstudio.org${pneu.image_url}`}
                                    className='image-pneu'
                                />
                            }
