import React, { useEffect, useState} from 'react';
import { MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBRow, MDBTestimonial} from 'mdbreact';
import styled from 'styled-components'

//icons
import pneus from '../assets/pneus.png'
import commande from '../assets/commandes.png'
import payment from '../assets/payement.png'

const Styles = styled.div`
    .coordonnee{
        display:flex;
        flex-direction:row;
        align-items:center;
        justify-content:center;
        width:100%;
        height:100%;
        padding: 2% 0;
    }
    .card-coordonnee{
        display:flex;
        flex-direction:column;
        align-items:center;
        justify-content:center;
        width:35%;
        height:100%;
        border-radius:10px;
    }
    .card-col-1{
        display:flex;
        flex-direction:row;
        align-items:center;
        justify-content:center;
        width:100%;
        height:100%;
        background:transparent;
        border-radius:50%
    }
    .card-col-2{
        display:flex;
        flex-direction:column;
        align-items:center;
        justify-content:center;
        width:100%;
        height:100%;
    }
    .card-col-3{
        display:flex;
        flex-direction:column;
        align-items:flex-start;
        justify-content:center;
        width:5%;
        height:100%;
        padding-left:5%;
    }
`

const Coordonnee = (props) => {
    return(
        <Styles>
             <div className='coordonnee'>
                <div className='card-coordonnee'>
                    <div className='card-col-1'>
                        <h1>1</h1>
                        <img
                            src={pneus}
                            width="98"
                            height="78"
                            className="logo"
                            alt="Logo Pneu service"
                        />
                    </div>
                    <div className='card-col-2'>
                        <p style={{fontSize:'1.3em', fontWeight:'600', color:'#888', margin:'0', fontFamily: 'Roboto, sans-serif'}}>1.Choisissez vos pneus </p>
                        <p style={{fontSize:'1em', fontWeight:'600', color:'#999', fontFamily: 'Roboto, sans-serif'}}>Parmi un large choix </p>
                    </div>
                </div>
                <div className='card-coordonnee'>
                    <div className='card-col-1'>
                        <img
                            src={commande}
                            width="88"
                            height="84"
                            className="logo"
                            alt="Logo Pneu service"
                        />
                    </div>
                    <div className='card-col-2'>
                    <p style={{fontSize:'1.3em', fontWeight:'600', color:'#888', margin:'0', fontFamily: 'Roboto, sans-serif'}}>2.Faites votre commande.</p>
                        <p style={{fontSize:'1em', fontWeight:'600', color:'#999',fontFamily: 'Roboto, sans-serif'}}>En precisant le nombre de pneu</p>
                    </div>
                </div>
                <div className='card-coordonnee'>
                    <div className='card-col-1'>
                        <img
                            src={payment}
                            width="83"
                            height="80"
                            className="logo"
                            alt="Logo Pneu service"
                        />
                    </div>
                    <div className='card-col-2'>
                    <p style={{fontSize:'1.3em', fontWeight:'600', color:'#888', margin:'0', fontFamily: 'Roboto, sans-serif'}}>3.Payez à la livraison </p>
                        <p style={{fontSize:'1em', fontWeight:'600', color:'#999', fontFamily: 'Roboto, sans-serif'}}>ou au centre de montage agrée.</p>
                    </div>
                </div>
            </div>
        </Styles>
    )
}

export default Coordonnee;
