import React from 'react';
import { Link, useHistory } from 'react-router-dom';
import styled from 'styled-components';
import {Nav, Navbar} from 'react-bootstrap'

//logo
import logo from '../assets/logo-rouge-blanc.png'

//images
import dark from '../assets/dark1.jpg'

const Styles = styled.div`
.navbar-links{
    color:white;
    font-size:1em;
    font-family:'Roboto', sans-serif; 
        &:hover{
        border-bottom: solid rgba(255, 0, 0, 0.5);
        text-decoration:none;
    }
}
.scrolled{ 
    position : fixed;
    background:url(${dark});
    width:100vw;
    padding:0 2%;
    z-index:1;
    transition:0.9s;
    @media only screen and (max-width: 768px) {
          width:100vw;
      }
   }
`;

useEffect(
    () => {
        window.addEventListener('scroll', () => {
            const isTop = window.scrollY > 0;
            const nav = document.getElementById('nav');
            if (isTop){
                nav.classList.add('scrolled');
            }else{
                nav.classList.remove('scrolled');
            }
        })
        return () => {
            //window.removeEventListener('scroll')
        }
    }, []
);

function NavBar() {

    return(
        <React.Fragment>
            <Styles>
                <Navbar expand="lg" bg="none" variant="light">
                    <Navbar.Brand>
                        <img
                            src={logo}
                            width="140"
                            height="85"
                            className="d-inline-block align-top"
                            alt="React Bootstrap logo"
                            style={{marginRight:'100px'}}
                        />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link className='promo-link'><Link className='navbar-links' to='/promos'>Promo</Link></Nav.Link>
                            <Nav.Link id='nav7'><Link className='navbar-links' >Montage</Link></Nav.Link>
                        </Nav>
                        <Nav className="ml-auto" >
                            <Nav.Link id='nav1' ><Link className='navbar-links' to='/'>Pneu Auto</Link></Nav.Link>
                            <Nav.Link id='nav2'><Link className='navbar-links' to='/PneuMoto'>Pneu Moto</Link></Nav.Link>
                            <Nav.Link id='nav3'><Link className='navbar-links' >Pneu Genie Civile</Link></Nav.Link>
                            <Nav.Link id='nav4'><Link className='navbar-links' >Pneu Manutation</Link></Nav.Link>
                            <Nav.Link id='nav5'><Link className='navbar-links' to='/PneuAgro'>Pneu Moto</Link></Nav.Link>
                            <Nav.Link id='nav6'><Link className='navbar-links' >Pneu Poids Lourds</Link></Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
            </Styles>
        </React.Fragment>
    )
}

export default NavBar;
