import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import {InputGroup, FormControl, Button} from 'react-bootstrap'
import axios from 'axios'
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';

//components
import Navbar from '../components/Navbar'

//images 
import map from '../assets/map2.jpg'
import garage from '../assets/garage.jpg'

//icons
import {FiSearch} from 'react-icons/fi'

const top100Films = [
    { title: 'The Shawshank Redemption', year: 1994 },
    { title: 'The Godfather', year: 1972 },
    { title: 'The Godfather: Part II', year: 1974 },
    { title: 'The Dark Knight', year: 2008 },
    { title: '12 Angry Men', year: 1957 },
    { title: "Schindler's List", year: 1993 },
    { title: 'Pulp Fiction', year: 1994 },
    { title: 'The Lord of the Rings: The Return of the King', year: 2003 },
    { title: 'The Good, the Bad and the Ugly', year: 1966 },
    { title: 'Fight Club', year: 1999 },
    { title: 'The Lord of the Rings: The Fellowship of the Ring', year: 2001 },
    { title: 'Star Wars: Episode V - The Empire Strikes Back', year: 1980 },
    { title: 'Forrest Gump', year: 1994 },
    { title: 'Inception', year: 2010 },
    { title: 'The Lord of the Rings: The Two Towers', year: 2002 }
]
const Styles = styled.div`
    height:100%;
    display: grid;
    grid-template-columns: 1fr repeat(12, minmax(auto, 4.2rem)) 1fr;
    grid-template-rows:5.8rem 28.5rem 5rem;
    gap : 0 2rem;

    .navbar-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 2;
        z-index:10;
    }
    .promo-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 4;
        display:flex;
        flex-direction:row;
        justify-content:center;
        align-items:center;
    }
    .promo-left-side{
         display:flex;
         width: 40%;
         height: 100%;
         background: -webkit-linear-gradient( rgba(0, 0, 0, 0.7) 100%,transparent 100%), url(${map});
         background-size:cover;
         align-items:center;
         justify-content:flex-end;
         box-shadow:50px rgba(0,0,0, 0.253);
    }
    .info-promo{
       width:100%;
       height: 100%;
       display:flex;
       flex-direction:column;
       justify-content:center;
       align-items:center;
       color: white;
    }
    .promo-right-side{
        display:flex;
        width: 60%;
        height: 100%;
        background: -webkit-linear-gradient( rgba(0, 0, 0, 0.4) 100%,transparent 100%), url(${garage});
        background-size:cover;
        align-items:center;
        justify-content:flex-start;
    }
    a{
        color:white;
        text-decoration:none;
    }
    .input-group {
      width:80%;
      height:9vh;
      margin-bottom:10px;
    }
    .form-control{
      height:100%;
    }
    #free-solo-demo{
        background-color:#fff !important;
    }

    div.MuiAutocomplete-root{
        width:90% !important;
        background-color : #fff !important;
        border-radius:3px !important;
    }

`;

function CentreMentage(){
    const [ville, setVille] =useState('Oran')
    const [centreMontage, setCentreMontage] = useState([])
    const [villes, setVilles] = useState([
        {ville : 'Oran '},
        {ville : 'Alger'},
        {ville : 'Tlemcen'},
        {ville : 'Sidi-belabes'},
        {ville : 'Mostaganem'},
        {ville : 'Ain Temouchent'},
        {ville : 'Mascara'},
        {ville : 'Tiaret'},
        {ville : 'El-chlef'},
        {ville : 'Relizene'},
        {ville : 'Saida'},
        {ville : 'El-Eulma'},
        {ville : 'Annaba'},
        {ville : 'Constantine'},
        {ville : 'Ourgla'}
    ])

    //handle change from inputs
    const onVilleChange = (event, values) => {
        setVille(values.toString())
        console.log(values)
      }

      useEffect(()=>{
        axios.post(`${process.env.REACT_APP_API_URL}/get/centre/montage`, {
            ville
        })
        .then(res => {
            console.log(ville)
            console.log(res.data)
            setCentreMontage(res.data)
        })
        .catch(err => {
            console.log(err)
        })
      },[ville])

      function handleSearch(){
        
      }

    return(
        <React.Fragment>
            <Styles>
                <div className='navbar-item'>
                    <Navbar bg="none" variant="light"/>
                </div>
                <div className='promo-item'>
                    <div className='promo-left-side'>
                        <div className='info-promo'>
                            <div style={{width:'80%', display:'flex', justifyContent:'left'}}>
                               <h5 style={{textAlign:'left'}}>Rechercher un centre de montage</h5>
                            </div>
                            <InputGroup >
                                <Autocomplete
                                    id="free-solo-demo"
                                    defaultValue={villes[0].ville}
                                    onChange = {onVilleChange}
                                    options={villes.map((option) => option.ville)}
                                    renderInput={(params) => (
                                    <TextField {...params} placeholder="Ville (ex : Oran, Alger...)" margin="none" variant="outlined" style={{padding:'0'}}/>
                                    )}
                                />
                                <InputGroup.Append>
                                   <Button variant="outline-secondary" onClick={handleSearch}>
                                       <FiSearch/>
                                    </Button>
                                </InputGroup.Append>
                            </InputGroup>
                            <div style={{width:'80%', display:'flex', justifyContent:'left'}}>
                               <h9 style={{textAlign:'center', padding:'0 5px'}}>Trouver le centre de montage pneu qui vous convient parmi les meilleurs centres en Algerie...</h9>
                            </div>
                        </div>
                    </div>
                    <div className='promo-right-side'>
                    
                    </div>
                </div>
            </Styles>           
        </React.Fragment>
    )
}
export default CentreMentage;
