import React, {useState, useEffect} from 'react';
import styled from 'styled-components';

//icons
import {FaCloudShowersHeavy, FaGasPump} from 'react-icons/fa'

//images
import default_img from '../assets/default_image.png'


const Styles = styled.div`
   padding : 0 8%;
   box-shadow:0 20px rgba(0,0,0, 0.253);
`;

const Card_pneu = styled.div`
    display:flex;
    flex-direction: row;
    box-shadow:5px 10px 20px 1px rgba(0,0,0, 0.253);
    background:#F5F5F5;
    border-radius:10px;
    margin-top: 1% ;

    .card-img{
        display:flex;
        flex-direction:column;
        width:15%;
    }
    .default-img{
        position: relative;
        height:100%;
        width: 100%;
        color:#333;
    }
    .card-info-1{}
    .card-info-2{}

    .image {
        display: block;
        width: 100%;
        height: auto;
    }

    .overlay {
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        height: 100%;
        width: 100%;
        opacity: 0.7;
        transition: .5s ease;
        background-color: #fff;
    }
    .overlay-1{
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        height: 100%;
        width: 100%;
        transition: .5s ease;
    }
    .text {
        color: #000;
        font-size: 24px;
        font-weight:500;
        position: absolute;
        top: 50%;
        left: 50%;
        -webkit-transform: translate(-50%, -50%);
        -ms-transform: translate(-50%, -50%);
        transform: translate(-50%, -50%);
        text-align: center;
    }
`;

function SearchResult(props){
    return(
        <React.Fragment>
            <Styles>
    <h5 style={{fontWeight:'600', color:'#444'}}>Notre selection de pneus : {props.dimensions.largeur}/{props.dimensions.hauteur} R{props.dimensions.diametre} {props.dimensions.charge} {props.dimensions.vitesse}</h5>
                <hr/>
                {props.resultat.map( (res) => 
                <Card_pneu>
                    <div className='card-img'>
                        {res.image_url === null ? 
                        <div className='default-img'>
                           <img
                            src={`${default_img }`}
                            width='150'
                            height='200'
                            className='image'
                           />
                           <div className="overlay">
                                {res.marque_img === null ? null :
                                    <img
                                        src={`https://www.monsterstudio.org${res.marque_img}`}
                                        width='150'
                                        height='30'
                                    />
                                }
                                <div className="text">Image Non Disponible</div>
                            </div>
                        </div>
                         :
                        <div className='default-img'>
                            <img
                                src={`https://www.monsterstudio.org${res.image_url}`}
                                width='150'
                                height='200'
                            />
                            <div className="overlay-1">
                                {res.marque_img === null ? null :
                                    <img
                                        src={`https://www.monsterstudio.org${res.marque_img}`}
                                        width='150'
                                        height='30'
                                    />
                                }
                            </div>
                        </div> 
                        }
                    </div>
                    <div className='card-info-1'>
                       <p style={{fontSize :'2em', fontWeight:'700', color:'#444' }}>{res.collection}</p>
                    <p style={{fontSize :'1.8em', fontWeight:'600', color:'#444' }}>{props.dimensions.largeur}/{props.dimensions.hauteur} R{props.dimensions.diametre} {props.dimensions.charge} {props.dimensions.vitesse}</p>
                       <p style={{fontSize :'1.8em', fontWeight:'600', color:'#444' }}>categorie : {res.type}</p>
                       <div className='card-info-2'>
                          <FaGasPump style={{fontSize :'1.8em', color:'#666' }}/>
                          <FaCloudShowersHeavy style={{fontSize :'1.8em', color:'#666' }}/>
                       </div>
                    </div>
                </Card_pneu>
                )}            
        </Styles>
        </React.Fragment>
    )
}

export default SearchResult;

/**
 * 
 * 
 */
