import React, {useState} from 'react';
import { Link, useHistory } from 'react-router-dom';
import {Nav, Navbar} from 'react-bootstrap'
import styled from 'styled-components';

import background from '../assets/photo-of-red-suv-on-dirt-road.jpg'
import logo from '../assets/PneuS-06.png'

//icons
import {GiPhone} from 'react-icons/gi'
import {MdMail} from 'react-icons/md'

const Styles = styled.div`
background: -webkit-linear-gradient( rgba(0, 0, 0, 0.4) 100%,transparent 100%), url(${background});
height:100vh;
background-size:cover;
background-position:center;

.navbar-links{
    color:white;
    font-weight:400;
    &:hover{
        text-decoration:none;
    }
}
a.nav-link{
    &:hover{
        background-color: rgba(255, 0, 0, 0.5);
        height:100%;
        text-decoration:none;
        border-radius:5px;
    }
}
.contact-navbar{
    display:flex;
    flex-direction:row;
    justify-content:flex-end;
    align-items:center;
    height:6%;
    margin:0;
    color:white;
    padding:0 4%;
}
.contact-icon{
    height:90%;
    width:6%;
    margin:0 2%;
}
.contact-text{
    padding-top:2%;
    transition:0.5s
}
`;

function Header() {  
    const [isShown, setIsShown] = useState(false)

    return(
       <React.Fragment>
           <Styles>
               <div className='contact-navbar'>
                   <GiPhone 
                     className='contact-icon'
                     onMouseEnter={() => console.log(isShown)}
                     onMouseLeave={() => console.log(isShown)}
                     />
                   {isShown &&(<p className='contact-text'>0557-932-082</p>) }
                    <MdMail className='contact-icon'/>
                    <p className='contact-text'>hammoumihanantel@gmail.com</p>
        
               </div>
                <Navbar expand="lg" bg="none" variant="dark">
                    <Navbar.Brand>
                    <img
                        src={logo}
                        width="120"
                        height="50"
                        className="d-inline-block align-top"
                        alt="React Bootstrap logo"
                    />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link><Link className='navbar-links' to='/'>Pneu Auto</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/promos'>Pneu Moto</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/promos'>Pneu Genie civile</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/promos'>Pneu Manutation</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/promos'>Pneu Agro</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/promos'>Pneu Poids lourd</Link></Nav.Link>
                        </Nav>
                        <Nav className="ml-auto">
                            <Nav.Link><Link className='navbar-links' to='/'>Promo</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/promos'>Montage</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/promos'>Marques</Link></Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
                <div className=''></div>
           </Styles>
       </React.Fragment> 
    )}

export default Header;
