import React, { useEffect, useState} from 'react';
import { MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBRow, MDBTestimonial} from 'mdbreact';
import styled from 'styled-components'

const Styles = styled.div`
.slider-row{
  display:flex;
  flex-direction:row;
}
.right-column{
    display:flex;
  flex-direction:column;
  justify-content:center;
  align-items:flex-start
}
.left-column{
  display:flex;
  flex-direction:column;
  justify-content:center;
  align-items:center;
  background:#fff;
  padding-left:1%;
}
.name{
   font-size:2em;
   font-weight:bold;
   color:#555;
}
.marque{
    font-size:1.5em;
   font-weight:bold;
   color:#333;
}
.prix{
    font-size:2.5em;
   font-weight:bold;
   color:#555;
}
.carousel-button{
    width:30%;
    height:20%;
    padding:1% 1%;
    background : rgba(199, 7, 7, 0.85);
    border:none;
    border-radius:10px;
    color:white;
    font-size:1.2em;
    font-weight:bold;
}
`;

const Product_Carousel = (props) => {
    return (
        <Styles>
            <MDBCarousel activeItem={1} length={5} slide={true} showControls={true} multiItem testimonial>
              <MDBCarouselInner>
                <MDBCarouselItem itemId="1">
                    <div className='slider-row'>
                        <div className='left-column'>
                            <img src={props.content[0].image}
                                alt=""
                            />
                        </div>
                        <div className='right-column'>
                            <h4 className='name'>{props.content[0].name}</h4>
                            <h6 className='marque'>{props.content[0].marque}</h6>
                            <p className='prix'>{props.content[0].prix}</p>
                            <button className='carousel-button' type='button'>Voir</button>
                        </div>
                    </div>
                </MDBCarouselItem>
                <MDBCarouselItem itemId="2">
                    <div className='slider-row'>
                        <div className='left-column'>
                            <img src={props.content[1].image}
                                alt=""
                            />
                        </div>
                        <div className='right-column'>
                            <h4 className='name'>{props.content[1].name}</h4>
                            <h6 className='marque'>{props.content[1].marque}</h6>
                            <p className='prix'>{props.content[1].prix}</p>
                            <button className='carousel-button' type='button'>Voir</button>
                        </div>
                    </div>  
                </MDBCarouselItem>
                <MDBCarouselItem itemId="3">
                    <div className='slider-row'>
                       <div className='left-column'>
                            <img src={props.content[2].image}
                                alt=""
                            />
                        </div>
                        <div className='right-column'>
                            <h4 className='name'>{props.content[2].name}</h4>
                            <h6 className='marque'>{props.content[2].marque}</h6>
                            <p className='prix'>{props.content[2].prix}</p>
                            <button className='carousel-button' type='button'>Voir</button>
                        </div>
                    </div>  
                </MDBCarouselItem>
                <MDBCarouselItem itemId="4">
                    <div className='slider-row'>
                        <div className='left-column'>
                            <img src={props.content[3].image}
                                alt=""
                            />
                        </div>
                        <div className='right-column'>
                            <h4 className='name'>{props.content[3].name}</h4>
                            <h6 className='marque'>{props.content[3].marque}</h6>
                            <p className='prix'>{props.content[3].prix}</p>
                            <button className='carousel-button' type='button'>Voir</button>
                        </div>
                    </div> 
                </MDBCarouselItem>
                <MDBCarouselItem itemId="5">
                    <div className='slider-row'>
                        <div className='left-column'>
                            <img src={props.content[4].image}
                            alt=""
                            />
                        </div>
                        <div className='right-column'>
                            <h4 className='name'>{props.content[4].name}</h4>
                            <h6 className='marque'>{props.content[4].marque}</h6>
                            <p className='prix'>{props.content[4].prix}</p>
                            <button className='carousel-button' type='button'>Voir</button>
                        </div>
                    </div> 
                </MDBCarouselItem>
            </MDBCarouselInner>
        </MDBCarousel>
        </Styles>
      );
  }
  
  export default Product_Carousel;


