import React, {useState, useEffect} from 'react';
import {useRouteMatch} from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios'

//components
import Navbar from '../components/Navbar'

const Styles = styled.div`
display:flex;
flex-direction:row;
justify-content:flex-start;
align-items:flex-start;
height:100vh;
padding:8%;

.left-side{
    padding-right:10%;
    width:50%;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:flex-end;
}

.right-side{
    padding-left:5%;
    width:50%;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:flex-start;
}
p{
    padding:0;
    margin:0
}

.designation{
    font-size:1.5em;
    font-weight:500;
    color:#333;
}

.titre{
    color:red;
    font-weight:500;
}

.caracteristique{
    font-size:1em;
    font-weight:500;
    color:#222;
}
`;

function Detail(){
    let match = useRouteMatch();
    const [pneu, setPneu] = useState([])
    
    useEffect(() => {
        var id = match.params.id_pneu
        axios.post(`${process.env.REACT_APP_API_URL}/pneu/details`, {
            id
        })
        .then(res => {
            console.log(res.data[0])
           setPneu(res.data[0])
        })
        .catch(err => {
            console.log(err)
        })
    }, []);
    return(
        <React.Fragment>
            <Navbar bg="dark" variant="dark"/>
            <Styles>
                <div className='left-side'>
                    <img
                        src={`https://www.monsterstudio.org${pneu.image_url}`}
                        width='280'
                        height='330'
                        className='image'
                    />
                    <img
                        src={`https://www.monsterstudio.org${pneu.marque_img}`}
                        width='150'
                        height='30'
                    />
                </div>
                <div className='right-side'>
                    <p className='designation'>Pneu {pneu.designation_pneu}</p>
                    <p className='titre'>Modele</p>
                    <p className='caracteristique'>{pneu.collection}</p>
                    <p className='titre'>Dimension</p>
                    <p className='caracteristique'>{pneu.largeur}/ {pneu.hauteur} R{pneu.diametre}  {pneu.charge} {pneu.vitesse}</p>
                    <p className='titre'>Gamme</p>
                    <p className='caracteristique'>competition</p>
                    <p className='titre'>Usage</p>
                    <p className='caracteristique'>trackday (semi-slick)</p>
                    <p className='titre'>Point Fort</p>
                    <p className='caracteristique'>Design optimise le grip et le drainage</p>
                    <p className='caracteristique'>Carcasse renforcée dans les flancs</p>
                    <p className='caracteristique'>Nouveau mélange de gomme</p>
                </div> 
            </Styles>            
        </React.Fragment>
    )
    
}
export default Detail; 

/**
 * 
    {pneu.adherence}
    {pneu.carburant}
    {pneu.bruit}

    {pneu.designation_pneu}
    {pneu.marque}
    {pneu.type}        
 */
