import React, {useEffect, useState} from 'react';
import { Link, useHistory } from 'react-router-dom';
import styled from 'styled-components';
import {Nav, Navbar} from 'react-bootstrap'

//logo
import logo from '../assets/logo-rouge-blanc.png'

//images
import dark from '../assets/dark1.jpg'

const Styles = styled.div`

.NavbarItems{
    background: transparent;
    width:100vw;
    height:10vh;
    padding:0 2%;
    transition:0.9s;
    display:flex;
    flex-direction:row;
    align-items:center;
}

.scrolled{ 
    position : fixed;
    background: #262626 /*url(${dark})*/;
    background-size:cover;
    width:100vw;
    padding:0 2%;
    z-index:11;
    transition:0.9s;
    @media only screen and (max-width: 768px) {
          width:100vw;
      }
   }

.logo{
    width:15%;
    height:95%;
    justify-self:start;
} 

.menu-icon{
    color:white;
}  



`;



function NavBar(props) {
    //search styling
    const [key, setKey] = useState("auto");
    const ActiveStyle = {
        fontSize:'1em',
        fontFamily:'NTR, sans-serif ' ,
    };
    const inActiveStyle = {
        ...ActiveStyle,
        background:'#666',
        color: "white",
        fontSize:'1em',
        fontFamily:'NTR, sans-serif ' 

    };

    useEffect(
        () => {
            window.addEventListener('scroll', () => {
                const isTop = window.scrollY > 0;
                const nav = document.getElementById('nav');
                if (isTop){
                    nav.classList.add('scrolled');
                }else{
                    nav.classList.remove('scrolled');
                }
            })
            return () => {
                //window.removeEventListener('scroll')
            }
        }, []
    );

    return(
        <React.Fragment>
            <Styles>
                <nav className='NavbarItems' id='nav'>
                    <img className='logo' src={`${logo}`}/>
                    <div className='menu-icon'>
                         <i className="fas fa-bars"></i>
                    </div>
                    <ul className='navbar-links-list'>
                        <li><Link className=' navbar-link' to='/promos'>Promo</Link></li>
                        <li><Link className=' navbar-link' >Montage</Link></li>
                        <li><Link className=' navbar-link' to='/'>Pneu Auto</Link></li>
                        <li><Link className=' navbar-link' to='/pneu-moto'>Pneu Moto</Link></li>
                        <li><Link className=' navbar-link' >Pneu Genie Civile</Link></li>
                        <li><Link className=' navbar-link' to='/pneu-poids-lourds'>Pneu Poids Lourds</Link></li>
                        <li><Link className=' navbar-link' to='/pneu-agro'>Pneu Agricole</Link></li>
                    </ul>

                </nav>
            </Styles>
        </React.Fragment>
    )
}

export default NavBar;


