import React, {useState} from 'react';
import styled from 'styled-components';
import {InputGroup, Button} from 'react-bootstrap'
import axios from 'axios'
import { ReactSearchAutocomplete } from 'react-search-autocomplete';


//components
import Navbar from '../components/Navbar'

//images 
import map from '../assets/map2.jpg'
import garage from '../assets/garage.jpg'

//icons
import {FiSearch} from 'react-icons/fi'
const Styles = styled.div`
    height:100%;
    display: grid;
    grid-template-columns: 1fr repeat(12, minmax(auto, 4.2rem)) 1fr;
    grid-template-rows:5.8rem 28.5rem 5rem;
    gap : 0 2rem;

    .navbar-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 2;
        z-index:10;
    }
    .promo-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 4;
        display:flex;
        flex-direction:row;
        justify-content:center;
        align-items:center;
    }
    .promo-left-side{
         display:flex;
         width: 40%;
         height: 100%;
         background: -webkit-linear-gradient( rgba(0, 0, 0, 0.7) 100%,transparent 100%), url(${map});
         background-size:cover;
         align-items:center;
         justify-content:flex-end;
         box-shadow:50px rgba(0,0,0, 0.253);
    }
    .info-promo{
       width:100%;
       height: 100%;
       display:flex;
       flex-direction:column;
       justify-content:center;
       align-items:center;
       color: white;
    }
    .promo-right-side{
        display:flex;
        width: 60%;
        height: 100%;
        background: -webkit-linear-gradient( rgba(0, 0, 0, 0.4) 100%,transparent 100%), url(${garage});
        background-size:cover;
        align-items:center;
        justify-content:flex-start;
    }
    a{
        color:white;
        text-decoration:none;
    }
    .input-group {
      width:80%;
      height:9vh;
      margin-bottom:10px;
    }
    .form-control{
      height:100%;
    }
    #free-solo-demo{
        background-color:#fff !important;
    }

    div.MuiAutocomplete-root{
        width:90% !important;
        background-color : #fff !important;
        border-radius:3px !important;
    }
    input{
        width:200px !important;
    }

`;

function CentreMentage(){



    const [ville, setVille] =useState('Oran')
    const [centreMontage, setCentreMontage] = useState([])
    const [villes, setVilles] = useState([
        {ville : 'Oran '},
        {ville : 'Alger'},
        {ville : 'Tlemcen'},
        {ville : 'Sidi-belabes'},
        {ville : 'Mostaganem'},
        {ville : 'Ain Temouchent'},
        {ville : 'Mascara'},
        {ville : 'Tiaret'},
        {ville : 'El-chlef'},
        {ville : 'Relizene'},
        {ville : 'Saida'},
        {ville : 'El-Eulma'},
        {ville : 'Annaba'},
        {ville : 'Constantine'},
        {ville : 'Ourgla'}
    ])

    const handleOnSelect = item => {
        // the item selected
        console.log(item);
      }

      const handleOnFocus = () => {
        console.log("Focused");
      }

      const handleOnSearch = (string, cached) => {
        // onSearch returns the string searched and if
        // the values are cached. If the values are cached
        // "cached" contains the cached values, if not, returns false
        console.log(string, cached);
      }

    function handleSearch(){
        axios.post(`${process.env.REACT_APP_API_URL}/get/centre/montage`, {
            ville
        })
        .then(res => {
            console.log(res.data)
            setCentreMontage(res.data)
        })
        .catch(err => {
            console.log(err)
        })
    }


    return(
        <React.Fragment>
            <Styles>
                <div className='navbar-item'>
                    <Navbar bg="none" variant="light"/>
                </div>
                <div className='promo-item'>
                    <div className='promo-left-side'>
                        <div className='info-promo'>
                            <div style={{width:'80%', display:'flex', justifyContent:'left'}}>
                               <h5 style={{textAlign:'left'}}>Rechercher un centre de montage</h5>
                            </div>
                            <InputGroup >
                            <ReactSearchAutocomplete
                                items={villes}
                                onSearch={handleOnSearch}
                                onSelect={handleOnSelect}
                                onFocus={handleOnFocus}
                                autoFocus
                                placeholder = 'Ville (ex : Oran, Alger...)'
                                styling = {
                                    {
                                        height: "44px",
                                        width:'200px',
                                        border: "1px solid #dfe1e5",
                                        borderRadius: "24px",
                                        backgroundColor: "white",
                                        boxShadow: "rgba(32, 33, 36, 0.28) 0px 1px 6px 0px",
                                        hoverBackgroundColor: "#eee",
                                        color: "#212121",
                                        fontSize: "16px",
                                        fontFamily: "Arial",
                                        iconColor: "grey",
                                        lineColor: "rgb(232, 234, 237)",
                                        placeholderColor: "grey"
                                    }
                                }
                            />

                                <InputGroup.Append>
                                   <Button variant="outline-secondary" onClick={handleSearch}>
                                       <FiSearch/>
                                    </Button>
                                </InputGroup.Append>
                            </InputGroup>
                            <div style={{width:'80%', display:'flex', justifyContent:'left'}}>
                               <h9 style={{textAlign:'center', padding:'0 5px'}}>Trouver le centre de montage pneu qui vous convient parmi les meilleurs centres en Algerie...</h9>
                            </div>
                        </div>
                    </div>
                    <div className='promo-right-side'>
                    
                    </div>
                </div>
            </Styles>           
        </React.Fragment>
    )
}
export default CentreMentage; 


/**
 * <Autocomplete
        id="free-solo-demo"
        options={villes.map((option) => option.ville)}
        renderInput={(params) => (
        <TextField InputProps={{ ...params.InputProps, type: 'search' }} placeholder="Ville (ex : Oran, Alger...)" margin="none" variant="outlined" style={{padding:'0'}} ville={ville} onChange={handleChange}/>
        )}
    />
 * 
 * 
 */
