import React, { useEffect, useState} from 'react';
import { Link, useHistory } from 'react-router-dom';
import styled from 'styled-components';
import { useCycle } from "framer-motion";

//component
import Product_Carousel from '../components/Carousel'
import Recommandation from '../components/Recommandation'
import Header from '../components/Header'
import Navbar from '../components/Navbar'

const Styles = styled.div`
display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-template-rows: repeat(4, 100px);
  grid-template-areas: "navbar navbar navbar navbar"
                       "main main sidebar sidebar"
                        "main main sidebar sidebar"
                       "footer footer footer footer";
  grid-auto-flow: dense;
  grid-gap: 10px;

.griditem1 {
  grid-area: navbar;
}

.griditem2 {
  grid-area: main;
}

.griditem3 {
  grid-area: sidebar;
}

.griditem4 {
  grid-column-start: main-end;
  grid-column-end: footer-start;
  background-color: rgba(250, 0, 0, .5);
  grid-row: 3;
}

.griditem5 {
  background-color: rgba(250, 0, 0, .5);
  grid-column-start: sidebar-start; /* only takes up one cell because grid-column-end default value is one */
  grid-row-start: sidebar-start;
}

.griditem6 {
  grid-area: footer;
}

.marques{
    padding:0 7%;
    margin: 5% 0;
}
.titre-marques{
    font-size:2em;
    font-weight:600;
    color:#555;
    font-family: 'Teko', sans-serif;
}

`;

function Home(props) {
    let history = useHistory()

    
    useEffect(() => {
        props.setNavigation(history.location.pathname)
      }, []);
    return(
        <React.Fragment>
            <Styles>
                
                <Recommandation/>
                <div className='marques'>
                    <p className='titre-marques'>Nos marques de Pneu Auto</p>
                    <Product_Carousel/>
                </div>
            </Styles>
        </React.Fragment>
    )
}

export default Home;

