import React, {useState, useEffect} from 'react';
import { Link, useRouteMatch, Route } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios'

//components
import Navbar from '../components/Navbar'

const Styles = styled.div``;

function Detail(){
    let match = useRouteMatch();
    const [pneu, setPneu] = useState()
    
    useEffect(() => {
        var id = match.params.id_pneu
        axios.post(`${process.env.REACT_APP_API_URL}/pneu/details`, {
            id
        })
        .then(res => {
            console.log(res.data)
           setPneu(res.data)
        })
        .catch(err => {
            console.log(err)
        })
    }, []);
    return(
        <React.Fragment>
            <Navbar />
            <h1>{pneu.collection}</h1>
            <h1>{pneu.designation_pneu}</h1>
            <h1>{pneu.marque}</h1>
            <h1>{pneu.carburant}</h1>
            <h1>{pneu.adherence}</h1>
            <h1>{pneu.bruit}</h1>
            <h1>{pneu.image_url}</h1>
        </React.Fragment>
    )
    
}
export default Detail; 

/**
