import React, {useState, useEffect} from 'react';
import { Link, useRouteMatch, Route } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios'

//components
import Navbar from '../components/Navbar'

const Styles = styled.div``;

function Detail(){
    let match = useRouteMatch();
    const [pneu, setPneu] = useState([])
    
    useEffect(() => {
        var id = match.params.id_pneu
        axios.post(`${process.env.REACT_APP_API_URL}/pneu/details`, {
            id
        })
        .then(res => {
            console.log(res.data[0])
           setPneu(res.data[0])
        })
        .catch(err => {
            console.log(err)
        })
    }, []);
    return(
        <React.Fragment>
            <Styles>
                <Navbar />
                <div className='left-side'>
                    <img
                        src={`https://www.monsterstudio.org${pneu.image_url}`}
                        width='130'
                        height='180'
                        className='image'
                    />
                    <img
                        src={`https://www.monsterstudio.org${pneu.marque_img}`}
                        width='150'
                        height='30'
                    />
                </div>
                <div className='right-side'>
                    <h2>{pneu.collection}</h2>
                    <h5>{pneu.designation_pneu}</h5>
                    <h6>{pneu.largeur}/ {pneu.hauteur} R{pneu.diametre}  {pneu.charge} {pneu.vitesse}</h6>
                </div> 
            </Styles>            
        </React.Fragment>
    )
    
}
export default Detail; 

/**
 * 
             {pneu.adherence}
             {pneu.carburant}
             {pneu.bruit}
            
             {pneu.designation_pneu}
             
             
             
             {pneu.marque}
             
            
             {pneu.type}
             
             
 */
