            const pneusDim = res.data;
            let lars = []
            let hauts = []
            let diams = []
            let chars = []
            let vites = []
            let mars = []
            const unique = (value, index, self)=>{
                return self.indexOf(value) === index
            }
            pneusDim.map((pneudim)=>{
               lars.push(parseFloat(pneudim.largeur))
               hauts.push(pneudim.hauteur)
               diams.push(pneudim.diametre)
               chars.push(pneudim.charge)
               vites.push(pneudim.vitesse)
               mars.push(pneudim.marque)
            })
            const uniquelargeur = lars.filter(unique)
            const uniquehauteur = lars.filter(unique)
            const uniquediametre = lars.filter(unique)
            const uniquecharge = lars.filter(unique)
            const uniquevitesse = lars.filter(unique)
            const uniquemarque = lars.filter(unique)

            uniquelargeur.sort(function(a, b) {
                return a - b;
              });
              uniquehauteur.sort(function(a, b) {
                return a - b;
            });
            uniquediametre.sort(function(a, b) {
                return a - b;
            });
            uniquecharge.sort(function(a, b) {
                return a - b;
            });
            uniquevitesse.sort(function(a, b) {
                return a - b;
            });
            uniquemarque.sort(function(a, b) {
                return a - b;
            });
            setLargeurs(uniquelargeur)
            setHauteurs(uniquehauteur)
            setDiametres(uniquediametre)
            setCharges(uniquecharge)
            setVitesse(uniquevitesse)
            setMarques(uniquemarque)     
        })
        .catch(err => {
            console.log(err)
        })
    }, []);

