import React, {useState, useEffect} from 'react';
import {useRouteMatch} from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios'

//components
import Navbar from '../components/Navbar'

const Styles = styled.div`
display:flex;
flex-direction:row;
justify-content:flex-start;
align-items:flex-start;
height:100vh;
padding:8%;

.left-side{
    display:flex;
    flex-direction:column;
    justify-content:flex-start;
    align-items:center;
}

.right-side{
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;
}
`;

function Detail(){
    let match = useRouteMatch();
    const [pneu, setPneu] = useState([])
    
    useEffect(() => {
        var id = match.params.id_pneu
        axios.post(`${process.env.REACT_APP_API_URL}/pneu/details`, {
            id
        })
        .then(res => {
            console.log(res.data[0])
           setPneu(res.data[0])
        })
        .catch(err => {
            console.log(err)
        })
    }, []);
    return(
        <React.Fragment>
            <Navbar bg="dark" variant="dark"/>
            <Styles>
                <div className='left-side'>
                    <img
                        src={`https://www.monsterstudio.org${pneu.image_url}`}
                        width='280'
                        height='330'
                        className='image'
                    />
                    <img
                        src={`https://www.monsterstudio.org${pneu.marque_img}`}
                        width='150'
                        height='30'
                    />
                </div>
                <div className='right-side'>
                    <h5>Pneu {pneu.designation_pneu}</h5>
                    <h6>Modele</h6>
                    <h2>{pneu.collection}</h2>
                    <h6>Dimension</h6>
                    <h6>{pneu.largeur}/ {pneu.hauteur} R{pneu.diametre}  {pneu.charge} {pneu.vitesse}</h6>
                    <h6>Gamme</h6>
                    <h2>competition</h2>
                    <h6>Usage</h6>
                    <h2>trackday (semi-slick)</h2>
                    <h6>Point Fort</h6>
                    <h5>Design optimise le grip et le drainage</h5>
                    <h5>Carcasse renforcée dans les flancs</h5>
                    <h5>Nouveau mélange de gomme</h5>
                </div> 
            </Styles>            
        </React.Fragment>
    )
    
}
export default Detail; 

/**
 * 
    {pneu.adherence}
    {pneu.carburant}
    {pneu.bruit}

    {pneu.designation_pneu}
    {pneu.marque}
    {pneu.type}        
 */
