import React, { useEffect, useState} from 'react';
import { MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBRow, MDBTestimonial} from 'mdbreact';
import styled from 'styled-components'

const Styles = styled.div`
.slider-row{
  display:flex;
  flex-direction:row;
}
.right-column{
    display:flex;
  flex-direction:column;
  justify-content:center;
  align-items:flex-start
}
.left-column{
  display:flex;
  flex-direction:column;
  justify-content:center;
  align-items:center;
  background:#fff;
  padding-left:1%;
}
.name{
   font-size:1.5em;
   font-weight:bold;
   color:#666;
}
.marque{
    font-size:1.2em;
   font-weight:bold;
   color:#333;
}
.prix{
    font-size:2.5em;
   font-weight:bold;
   color:#555;
}
.carousel-button{
    width:30%;
    height:20%;
    padding:1% 1%;
    background : rgba(199, 7, 7, 0.85);
    border:none;
    border-radius:10px;
    color:white;
    font-size:1.2em;
    font-weight:bold;
}
.Carousel-row{
    display:flex;
    flex-direction:row;
    justify-content:center;
    align-items:center;
    padding:2% 7%;
}
.image-container{
    width:15%;
    height:100%;
}
`;

const Product_Carousel = (props) => {
    return (
        <Styles>
            <MDBCarousel activeItem={1} length={5} slide={true} showControls={true} multiItem testimonial>
              <MDBCarouselInner>
                <MDBCarouselItem itemId="1">
                    <div className='Carousel-row'>
                        <div className='image-container'>
                            <img
                            src={logo_transparent}
                            width='200'
                            height='80'
                            />
                        </div>
                    </div>
                </MDBCarouselItem>
                <MDBCarouselItem itemId="2">
                    
                </MDBCarouselItem>
                <MDBCarouselItem itemId="3">
                   
                </MDBCarouselItem>
            </MDBCarouselInner>
        </MDBCarousel>
        </Styles>
      );
  }
  
  export default Product_Carousel;


