import React, { useEffect, useState} from 'react';
import { Link, useHistory } from 'react-router-dom';
import styled from 'styled-components';
import { useCycle } from "framer-motion";

//component
import Product_Carousel from '../components/Carousel'
import Recommandation from '../components/Recommandation'
import Header from '../components/Header'
import Navbar from '../components/Navbar'

//images
import redCar from '../assets/red-car.jpg'
import pneus from '../assets/pneus.png'

const Styles = styled.div`
display: grid;
grid-template: repeat(16, [row] 1fr) / repeat(14, [col] 1fr);
margin-bottom:5%;

.item-1 {
    grid-column: col 1 / span 14;
    grid-row: row 1 / span 2 ;
    background: transparent;
    z-index:1;
}
.item-3 {
  grid-column: col 1 / span 5;
  grid-row: row 1 / span 11 ;
  background: #2F3945;
  opacity: 0.8;
  display:flex;
  flex-direction:row;
  justify-content:flex-end;
  align-items: center;
}

.item-4 {
  grid-column: col 6 / span 11;
  grid-row: row 1 / span 11 ;
  background: -webkit-linear-gradient( rgba(0, 0, 0, 0.3) 100%,transparent 100%), url(${redCar});
  background-size:cover;
  display:flex;
  flex-direction:row;
  justify-content:flex-start;
  align-items:center;
}

.item-5 {
  grid-column: col 2 / span 12;
  grid-row: row 9 / span 4;
  background: linear-gradient(90.84deg, #EF1A23 0.61%, #FB3C29 99.42%);  
  opacity: 0.8;
  color:white;
  z-index: 1;
}
.item-6 {
  grid-column: col 1 / span 14;
  grid-row: row 12 / span 4 ;
  background: linear-gradient(360deg, #FFFFFF 0%, rgba(255, 255, 255, 0.08) 57.24%, rgba(47, 57, 69, 0.54) 104.71%);
}
.item-7{
  grid-column: col 2 / span 12;
  grid-row: row 14 / span 4;
  background: transparent;
}
.item-8{
  grid-column: col 2 / span 12;
  grid-row: row 3 / span 5;
  background: url(${remise}) no-repeat;
  z-index:1;
}

.marques{
    padding:0 7%;
    margin: 5% 0;
}
.titre-marques{
    font-size:2em;
    font-weight:600;
    color:#555;
    font-family: 'Teko', sans-serif;
}

`;

function Home(props) {
    let history = useHistory()

    
    useEffect(() => {
        props.setNavigation(history.location.pathname)
      }, []);
    return(
        <React.Fragment>
            <Styles>
                <div className='item-1'>1</div>
                <div className='item-2'>2</div>
                <div className='item-3'>3</div>
                <div className='item-4'>4</div>
                <div className='item-5'>5</div>
                <div className='item-6'>6</div>
                <div className='item-7'>7</div>
                <div className='item-8'>8</div>


                <Navbar/>
                <Header/>
                <Recommandation/>
                <div className='marques'>
                    <p className='titre-marques'>Nos marques de Pneu Auto</p>
                    <Product_Carousel/>
                </div>
            </Styles>
        </React.Fragment>
    )
}

export default Home;

