import React, {useState, useEffect} from 'react';
import {Link} from 'react-router-dom'
import styled from 'styled-components';

//icons
import {FaCloudShowersHeavy, FaGasPump, FaVolumeUp, FaLink} from 'react-icons/fa'

//images
import default_img from '../assets/default_image.png'
import adherence from '../assets/adherence.jpg'

const Styles = styled.div`
   padding : 0 8%;
   scroll-behavior: smooth;

   .resultat{
    display:flex;
    flex-direction:row;
   }
   
   .Right-side{
     display:flex;
     flex-direction:column;
     width:75%;
   }

   .Left-side{
     width:25%;
     padding:1% 1% 3% 0;
   }

   
   .sticky {
    position: fixed;
    top: 0;
   }
   .image-adherence{
       width:100%;
       border-radius:10px;
   }
`;

const Card_pneu = styled.div`
    display:flex;
    flex-direction: row;
    align-items:center;
    box-shadow:5px 10px 20px 1px rgba(0,0,0, 0.253);
    border-radius:10px;
    margin: 1% 0;
    height:180px;
    width:100%;

    .card-img{
        display:flex;
        flex-direction:column;
        width:15%;
    }
    .default-img{
        position: relative;
        height:100%;
        width: 100%;
        color:#333;
    }
    .card-info-1{
        margin: 0 5%;
        width:35%;
    }
    .card-info-2{
        display:flex;
        flex-direction:row;
    }
    .card-info-3{
        display:flex;
        flex-direction:column;
        justify-content:flex-end;
        align-items:center;
        font-family: 'Heebo', sans-serif;
        color:#555;
        padding-bottom:5%;
        width:20%;
        height:90%;
    }
    .overlay {
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        height: 100%;
        width: 100%;
        opacity: 0.7;
        transition: .5s ease;
        background-color: #fff;
    }
    .overlay-1{
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        height: 100%;
        width: 100%;
        transition: .5s ease;
    }
    .text {
        color: #000;
        font-size: 1em;
        font-weight:600;
        position: absolute;
        top: 50%;
        left: 50%;
        -webkit-transform: translate(-50%, -50%);
        -ms-transform: translate(-50%, -50%);
        transform: translate(-50%, -50%);
        text-align: center;
    }
    .promotion{
        display:flex;
        flex-direction:column;
        justify-content:center;
        align-items:center;
        border-radius: 50%;
        background:#A7BF73;
        width:40px;
        height:40px;
        color:white;
        align-self:flex-start;
        justify-self:flex-start;
    }
    .price{
        display:flex;
        flex-direction:row;
    }
    .card-info-4{
        display:flex;
        flex-direction:column;
    }
    .details{
        color:#EF1A23;
        font-size:1.2em;
        background:transparent;
        border: 2px solid #EF1A23;
        text-decoration:none;
        padding : 5%;
        border-radius:5px;
    }
    .acheter{
        color:white;
        font-size:1.2em;
        background:#EF1A23;
        border: 1px solid;
        text-decoration:none;
        padding: 5%;
        border-radius:5px;
    }
`;

function SearchResult(props){   
    useEffect(() => {
       console.log(props.resultat)
    }, []);

    
    return(
        <React.Fragment>
            <Styles>
                {props.typeRecherche === 'dimension' ? 
                            <h5 style={{fontWeight:'600', color:'#444'}}>Notre selection de pneus : {props.dimensions.largeur}/{props.dimensions.hauteur} R{props.dimensions.diametre} {props.dimensions.charge} {props.dimensions.vitesse}</h5>
    :
    <h5 style={{fontWeight:'600', color:'#444'}}>Notre selection de pneus : {props.params.largeur}/{props.params.hauteur} R{props.params.diametre} {props.params.charge}</h5>

            }
                <hr/>
                <div className='resultat'>
                <div className=' Left-side' id='mySidebar'>
                        <img
                            src={`${adherence }`}
                            className='image-adherence'
                        />
    
                </div>
                <div className='Right-side'>
                    {props.resultat.map( (res) => 
                    <Card_pneu>
                        <div className='card-img'>
                            {res.image_url === null ? 
                            <div className='default-img'>
                            <img
                                src={`${default_img }`}
                                width='130'
                                height='180'
                                className='image'
                            />
                            <div className="overlay">
                                    {res.marque_img === null ? null :
                                        <img
                                            src={`https://www.monsterstudio.org${res.marque_img}`}
                                            width='150'
                                            height='30'
                                        />
                                    }
                                    <div className="text">Image Non Disponible</div>
                                </div>
                            </div>
                            :
                            <div className='default-img'>
                                <img
                                    src={`https://www.monsterstudio.org${res.image_url}`}
                                    width='130'
                                    height='180'
                                    className='image'
                                />
                                <div className="overlay-1">
                                    {res.marque_img === null ? null :
                                        <img
                                            src={`https://www.monsterstudio.org${res.marque_img}`}
                                            width='150'
                                            height='30'
                                        />
                                    }
                                </div>
                            </div> 
                            }
                        </div>
                        <div className='card-info-1'>
                        <p style={{fontSize :'1.3em', fontWeight:'700', color:'#444', margin:'0', padding:'0'}}>{res.collection}</p>
                        <p style={{fontSize :'1em', fontWeight:'600', color:'#444',margin:'0', padding:'0' }}>{props.dimensions.largeur}/{props.dimensions.hauteur} R{props.dimensions.diametre} {props.dimensions.charge} {res.vitesse}</p>
                        <p style={{fontSize :'1em', fontWeight:'600', color:'#444', margin:'0', padding:'0' }}>categorie : {res.type}</p>
                        <div className='card-info-2'>
                            <FaGasPump style={{fontSize :'1.8em', color:'#777', marginRight:'5%' }}/>
                            <p>{res.carburant}</p>
                            <FaCloudShowersHeavy style={{fontSize :'1.8em', color:'#777', marginRight:'5%' }}/>
                            <p>{res.adherence}</p>
                            <FaVolumeUp style={{fontSize :'1.8em', color:'#777', marginRight:'5%' }}/>
                                <p>{res.bruit}</p>
                        </div>
                        </div>
                        <div className = 'card-info-3'>
                            {res.price > 0 }
                            <div className='price'>
                                <div className='promotion'>
                                    <p style={{paddingTop:'15px'}}>50%</p>
                                </div>
                                <p style={{fontSize:'0.8em', fontWeight:'600', margin:'0', padding:'0', textDecoration:'line-through'}}>{res.price} DA</p>
                            </div>
                            <p style={{fontSize:'1.5em', fontWeight:'600', margin:'0', padding:'0'}}>{res.price} DA</p>
                            <p style={{fontSize:'0.8em', fontWeight:'400', margin:'0', padding:'0'}}>payer a la livraison</p>
                        </div>
                        <div className = 'card-info-4'>
                            <Link className='details'>Details</Link>
                            {res.price > 0 ?  
                                <Link className='acheter'>Commander</Link> :
                                <Link className='acheter'>Demander Devis</Link>
                            }
                        
                        </div>
                    </Card_pneu>
                    )}            
                </div>
                </div>
        </Styles>
        </React.Fragment>
    )
}

export default SearchResult;

/**
 * 
 * 
 */
