import React, {useState, useEffect} from 'react';
import {useRouteMatch} from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios'

//components
import Navbar from '../components/Navbar'

//images
import default_img from '../assets/default_image.png'

const Styles = styled.div`
display:flex;
flex-direction:row;
justify-content:flex-start;
align-items:flex-start;
height:100vh;
padding:3% 5%;

.left-side{
    padding-right:10%;
    width:50%;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:flex-end;
}

.image-pneu{
    width:50%;
    height:100%;
}

.default-img{
    display:flex;
    justify-content: flex-end;
    position: relative;
    height:100%;
    width: 100%;
    color:#333;
}

.text {
    color: #000;
    font-size: 1.2em;
    font-weight:600;
}

.overlay {
    display:flex;
    justify-content:flex-end;
    align-items:center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    height: 100%;
    width: 100%;
    opacity: 0.7;
    transition: .5s ease;
    background-color: #fff;
    padding-right:10%;
}

.image-marque-container{
    width:100%;
    display:flex;
    justify-content: flex-end;
}

.image-marque{
    width:50%;
    height:20%;
}

.right-side{
    width:50%;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:flex-start;
}
p{
    padding:0;
    margin:0
}

ul{
    padding:0;
    margin:0;
}

.info-container{
    display:flex;
    flex-direction:row;
    width:90%;
}

.info{
    width:50%;
    height:100%
}


.designation{
    font-size:1.3em;
    font-weight:500;
    color:#333;
    height:10%;
}

.titre{
    color:red;
    font-weight:500;
}

.caracteristique{
    font-size:1.2em;
    font-weight:400;
    color:#222;
}

.point-forts{
   font-size:0.8em;
}

.price-container{
    display:flex;
    flex-direction:column;
    height:100%;
    width:50%;
    padding-top:15%;
}

.promotion{
    display:flex;
    justify-content:center;
    align-items:center;
    border-radius: 50%;
    background:#A7BF73;
    width:60px;
    height:60px;
    color:white;
}
.price{
    display:flex;
    flex-direction:row;
}
`;

function Detail(){
    let match = useRouteMatch();
    const [pneu, setPneu] = useState([])
    
    useEffect(() => {
        var id = match.params.id_pneu
        axios.post(`${process.env.REACT_APP_API_URL}/pneu/details`, {
            id
        })
        .then(res => {
            console.log(res.data[0])
           setPneu(res.data[0])
        })
        .catch(err => {
            console.log(err)
        })
    }, []);
    return(
        <React.Fragment>
            <Navbar bg="dark" variant="dark"/>
            <Styles>
                <div className='left-side'>
                    {pneu.image_url === null ? 
                        <div className='default-img'>
                            <img
                                src={`${default_img }`}
                                className='image-pneu'
                            />
                            <div className="overlay">
                                <div className="text">Image Non Disponible</div>
                            </div>
                        </div>
                        :
                        <img
                        src={`https://www.monsterstudio.org${pneu.image_url}`}
                        className='image-pneu'
                        />
                    }
                    {pneu.marque_img === null ? null :
                        <div className='image-marque-container'>
                            <img
                                src={`https://www.monsterstudio.org${pneu.marque_img}`}
                                className='image-marque'
                            />
                        </div>
                    }
                </div>
                <div className='right-side'>
                    <p className='designation'>Pneu {pneu.designation_pneu}</p>
                    <div className='info-container'>
                        <div className='infos'>
                            <p className='titre'>Modele</p>
                            <p className='caracteristique'>{pneu.collection}</p>
                            <p className='titre'>Dimension</p>
                            <p className='caracteristique'>{pneu.largeur}/ {pneu.hauteur} R{pneu.diametre}  {pneu.charge} {pneu.vitesse}</p>
                            <p className='titre'>Gamme</p>
                            <p className='caracteristique'>competition</p>
                            <p className='titre'>Usage</p>
                            <p className='caracteristique'>trackday (semi-slick)</p>
                            <ul className='titre'>Point Fort</ul>
                            <li className='point-forts'>Design optimise le grip et le drainage</li>
                            <li className='point-forts'>Carcasse renforcée dans les flancs</li>
                            <li className='point-forts'>Nouveau mélange de gomme</li>
                        </div>
                        {pneu.price > 0 ? 
                                <div className='price-container'>
                                <div className='price'>
                                    <div className='promotion'>
                                        <p style={{fontSize:'1.5em'}}>50%</p>
                                    </div>
                                    <p style={{fontSize:'1em', fontWeight:'600', margin:'0', padding:'0', textDecoration:'line-through'}}>{pneu.price} DA</p>
                                </div>
                                <p style={{fontSize:'2.5em', fontWeight:'600', margin:'0', padding:'0', color:'#555'}}>{pneu.price} DA</p>
                                </div>
                        :null}
                    </div>  
                </div> 
            </Styles>            
        </React.Fragment>
    )
    
}
export default Detail; 

/**
 * 
    {pneu.adherence}
    {pneu.carburant}
    {pneu.bruit}

    {pneu.designation_pneu}
    {pneu.marque}
    {pneu.type}        
 */
