import React, {useState, useEffect} from 'react';
import { Link, useRouteMatch, Route } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios'

//components
import Navbar from '../components/Navbar'

const Styles = styled.div``;

function Detail(){
    let match = useRouteMatch();
    const [pneu, setPneu] = useState([])
    
    useEffect(() => {
        var id = match.params.id_pneu
        axios.post(`${process.env.REACT_APP_API_URL}/pneu/details`, {
            id
        })
        .then(res => {
            console.log(res.data[0])
           setPneu(res.data[0])
        })
        .catch(err => {
            console.log(err)
        })
    }, []);
    return(
        <React.Fragment>
            <Navbar />
             {pneu.collection}
             {pneu.adherence}
             {pneu.carburant}
             {pneu.bruit}
             {pneu.charge}
             {pneu.designation_pneu}
             {pneu.diametre}
             {pneu.hauteur}
             {pneu.largeur}
             {pneu.marque}
             <img
                src={`https://www.monsterstudio.org${pneu.marque_img}`}
                width='150'
                height='30'
            />
            <img
                src={`https://www.monsterstudio.org${res.image_url}`}
                width='130'
                height='180'
                className='image'
            />
             {pneu.type}
             {pneu.vitesse}
             
        </React.Fragment>
    )
    
}
export default Detail; 

/**
 * 
 */
