import React from 'react';
import styled from 'styled-components';

//components
import Navbar from '../components/Navbar'

//images 
import map from '../assets/map2.jpg'
import garage from '../assets/garage.jpg'


const Styles = styled.div`
    height:100%;
    display: grid;
    grid-template-columns: 1fr repeat(12, minmax(auto, 4.2rem)) 1fr;
    grid-template-rows:5.8rem 28.5rem 5rem;
    gap : 0 2rem;

    .navbar-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 2;
        z-index:10;
    }
    .promo-item{
        grid-column: 1 / span 14;
        grid-row: 1 / 4;
        display:flex;
        flex-direction:row;
        justify-content:center;
        align-items:center;
    }
    .promo-left-side{
         display:flex;
         width: 40%;
         height: 100%;
         background: -webkit-linear-gradient( rgba(0, 0, 0, 0.7) 100%,transparent 100%), url(${map});
         background-size:cover;
         align-items:center;
         justify-content:flex-end;
    }
    .info-promo{
       width:100%;
       height: 100%;
       display:flex;
       flex-direction:column;
       justify-content:center;
       align-items:center;
       color: white;
    }
    .promo-right-side{
        display:flex;
        width: 60%;
        height: 100%;
        background: -webkit-linear-gradient( rgba(0, 0, 0, 0.3) 100%,transparent 100%), url(${garage});
        background-size:cover;
        align-items:center;
        justify-content:flex-start;
    }

    
    a{
        color:white;
        text-decoration:none;
    }

`;

function CentreMentage(){
    return(
        <React.Fragment>
            <Styles>
                <div className='navbar-item'>
                    <Navbar bg="none" variant="light"/>
                </div>
                <div className='promo-item'>
                    <div className='promo-left-side'>
                    <div className='info-promo'>
                            
                    </div>
                    </div>
                    <div className='promo-right-side'>
                       
                    </div>
                </div>
            </Styles>           
        </React.Fragment>
    )
}
export default CentreMentage;
