import React from 'react';
import styled from 'styled-components';
import { motion} from "framer-motion";
import VisibilitySensor from "react-visibility-sensor";

//logo
import logo from '../assets/PneuS-03.png'

//image 
import mail from '../assets/Mail.png'
import facebook from '../assets/Facebook.png'
import phone from '../assets/phone.png'



const Styles = styled.div`
display:flex;
flex-direction:column;
align-items:center;
height:40vh;
font-size:16px;
.presentation{
    display:flex;
    flex-direction:row;
    align-items:center;
    justify-content:center;
    background-color:#eee;
    width:100%;
    height:30%;
    padding: 2% 0;
}
.coordonnee{
    display:flex;
    flex-direction:row;
    align-items:center;
    justify-content:center;
    width:100%;
    height:70%;
    padding: 0 2%;
    margin-top:1%;
}
.card-coordonnee{
    display:flex;
    flex-direction:row;
    align-items:center;
    justify-content:center;
    width:35%;
    height:100%;
    margin:0 2%;
    border-radius:10px;
}
.card-col-1{
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:center;
    width:25%;
    height:60%;
    background: rgba(199, 7, 7, 0.85);
    border-radius:50%
}
.card-col-2{
    display:flex;
    flex-direction:column;
    align-items:flex-start;
    justify-content:center;
    width:73%;
    height:100%;
    padding-left:5%;
}

`;
function underHeader() {
    return(
        <React.Fragment>
            <Styles>
                <VisibilitySensor>
                    {({ isVisible }) => {
                <motion.div 
                   className='coordonnee'
                   animate = {{opacity : isVisible ? 1 : 0, scale:isVisible ? 1 : 0}}
                   transition={{ ease: "easeOut", duration: 2 }}
                   >
                    <div className='card-coordonnee'>
                        <div className='card-col-1'>
                            <img
                                src={facebook}
                                width="29"
                                height="45"
                                className="logo"
                                alt="Logo Pneu service"
                            />
                        </div>
                        <div className='card-col-2'>
                            <p style={{fontSize:'1.3em', fontWeight:'900', color:'#999', margin:'0'}}>Facebook</p>
                            <p style={{fontSize:'0.8em', fontWeight:'500', color:'#555'}}> Trouvez toutes nos actualit√©s sur <br/>Facebook.com/Pneu service</p>
                        </div>
                    </div>
                    <div className='card-coordonnee'>
                        <div className='card-col-1'>
                            <img
                                src={mail}
                                width="58"
                                height="55"
                                className="logo"
                                alt="Logo Pneu service"
                            />
                        </div>
                        <div className='card-col-2'>
                            <p style={{fontSize:'1.3em', fontWeight:'900', color:'#999', margin:'0'}}>Par Email</p>
                            <p style={{fontSize:'0.8em', fontWeight:'500', color:'#555'}}>disponible 7J/7 & 24h/24 <br/>djelloul.boubekri@gmail.com</p>
                        </div>
                    </div>
                    <div className='card-coordonnee'>
                        <div className='card-col-1'>
                            <img
                                src={phone}
                                width="52"
                                height="55"
                                className="logo"
                                alt="Logo Pneu service"
                            />
                        </div>
                        <div className='card-col-2'>
                         <p style={{fontSize:'1.3em', fontWeight:'900', color:'#999', margin:'0'}}>+213 557 932 082</p>
                         <p style={{fontSize:'0.8em', fontWeight:'500', color:'#555'}}> Besoin d'un conseil ? <br/> Disponible Du samedi au jeudi de 09h √† 18h</p>
                        </div>
                    </div>
                </motion.div>
                  }}
                </VisibilitySensor>
                <div className='presentation'>
                    <p style={{fontSize:'1.2em', fontWeight:'500', color:'#888', marginRight:'0.5%'}}>Le pneu pas cher avec </p> 
                    <p style={{fontSize:'1.2em', fontWeight:'700', color:'rgba(199, 7, 7, 0.85)'}}>Pneuservice</p>
                    <p style={{fontSize:'1.2em', fontWeight:'500', color:'#888'}}>, Leader de la vente en ligne des pneus multimarque en Alg√©rie.</p>
                </div>
            </Styles>
        </React.Fragment>
    )
}

export default underHeader;

