import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import {Nav, Navbar, Tab, Row, Col, Form} from 'react-bootstrap'
import styled from 'styled-components';
import { motion} from "framer-motion";

//backgrounds
import backgroundAuto from '../assets/photo-of-red-suv-on-dirt-road.jpg'
import backgroundMoto from '../assets/red-and-white-dirt-bike.jpg'
import backgroundAgro from '../assets/toyota-4x4-sunset.jpg'
import backgroundPromo from '../assets/silver-car-with-dust.jpg'
import pneu from '../assets/Pneu.png'

//logo
import logo from '../assets/PneuS-03.png'

//icons
import {AiFillCaretRight} from 'react-icons/ai'


const Styles = styled.div`
font-size:16px;
height:100vh;
overflow:hidden;
.navbar-links{
    color:white;
    font-weight:400;
    &:hover{
        text-decoration:none;
    }
}
.promo-link{
    background: rgba(255, 0, 0, 0.5);
    border-radius:5px;
    font-weight:900
}
a.nav-link{
    &:hover{
        border-bottom: solid rgba(255, 0, 0, 0.5);
        text-decoration:none;
    }
}
.navbar-section{
   height:15%;
}
.landing-text{
    color: white;
    width:100%;
    height:50%;
    padding-left:12%;
    padding-bottom: 2%;
    display:flex;
    flex-direction:row;
    justify-content:flex-start;
    align-items:center;
    margin-left: 5%;
    margin-top: 5%;
}
.landing-text-scroll{
    color: white;
    width:100%;
    height:50%;
    padding-left:12%;
    padding-bottom: 2%;
    display:flex;
    flex-direction:row;
    justify-content:flex-start;
    align-items:center;
}
.landing-page-left-row{
    display:flex;
    flex-direction:column;
    justify-content:center;
}
.landing-big-title{
   padding:0;
   margin:0;
   display:flex;
   flex-direction:column;
}

.landing-button{
    font-size:1.5em;
    font-weight:600;
    background: #EF1A23;
    height:20%;
    width:50%;
    border-radius:5px;
    border:none;
    color:white;
}
.search-bar{
    height:35%;
    display:flex;
    flex-direction:row;
    justify-self:flex-end;
}
.search-bar-first-col{
   background: rgba(255, 0, 0, 0.4);
   width:80%;
   height:100%;
   color:white;
}
.search-first-row{
    display:flex;
    flex-direction:row;
    padding:1%;
    height:50%;
    justify-content:center;
    align-items:center;
}
.search-second-row{
    display:flex;
    flex-direction:row;
    justify-content:flex-end;
    align-items:flex-end;
    height:50%;
}
.search-button{
    font-size:1.5em;
    font-weight:600;
    background: #333;
    height:50px;
    width:20%;
    border-radius:5px;
    border:none;
    color:white;
    margin: 2% 3% 0 0;
}
.search-input{
    margin:0 1%;
    width: 12%;
}
.search-bar-second-col{
    position:relative;
    width:20%;
    height:100%;
    background: rgba(255, 0, 0, 0.4);
    display: inline-block;
    overflow:hidden;
}
.nav-tabs > a
 {
    font-weight:bolder;
    color:white;
}
.pneu-image{
    position:absolute;
    left:0%;
    top:0%;
}
`;

function Header(props) { 
    const [lastYPos, setLastYPos] = React.useState(0);
    const [isShown, setIsShown] = useState(true)
    const [key, setKey] = useState("dimension");
    const ActiveStyle = {
        textAlign: "left",
        background: "transparent",
        color: "white",
        fontWeight:'600',
        fontSize:'1.2em'
    };
    const inActiveStyle = {
        ...ActiveStyle,
        background: "rgba(0, 0, 0, 0.4)",
        color: "white",
        fontWeight:'600',
        fontSize:'1.2em'
    };
    
    useEffect(() => {

        function handleScroll() {
            const yPos = window.scrollY;
            const isScrollingUp = yPos < lastYPos;
            const text = document.getElementById('landingText');
      
            setIsShown(isScrollingUp);
            setLastYPos(yPos);

            if (isScrollingUp){
                text.classList.replace('landing-text-scroll', 'landing-text')
            }else{
                text.classList.replace('landing-text', 'landing-text-scroll')
            }
          }
        window.addEventListener('scroll', handleScroll, false )
        return () => {
            window.removeEventListener("scroll", handleScroll, false);
        }
      }, [lastYPos]);

    //background picker
    function backgroundSelect(){
        switch(props.navigation){
            case '/PneuMoto': return backgroundMoto;
            case '/PneuAgro': return backgroundAgro;
            case '/promos': return backgroundPromo;
            default: return backgroundAuto
        }
    }
    return(
       <React.Fragment>
           <Styles 
              style={{
                  background:`-webkit-linear-gradient( rgba(0, 0, 0, 0.5) 100%,transparent 100%), url(${backgroundSelect()})`, 
                  height:'100vh',
                  backgroundSize:'cover',
                  backgroundPosition:'center'
                  }}>
                <div className='navbar-section'>
                <Navbar expand="lg" bg="none" variant="dark">
                    <Navbar.Brand>
                    <img
                        src={logo}
                        width="120"
                        height="50"
                        className="d-inline-block align-top"
                        alt="React Bootstrap logo"
                    />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link><Link className='navbar-links' to='/'>Pneu Auto</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/PneuMoto'>Pneu Moto</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Pneu Genie civile</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Pneu Manutation</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' to='/PneuAgro'>Pneu Agro</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Pneu Poids lourd</Link></Nav.Link>
                        </Nav>
                        <Nav className="ml-auto">
                            <Nav.Link className='promo-link'><Link className='navbar-links' to='/promos'>Promo</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Montage</Link></Nav.Link>
                            <Nav.Link><Link className='navbar-links' >Marques</Link></Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
                </div>
                <motion.div 
                  className='landing-text'
                  id='landingText'
                  animate={{scale: isShown ? 1.2 : 0.9}}
                  transition={{ duration: 0.9 }}
                  >
                    <div className='landing-page-left-row'>
                        <div className='landing-big-title'>
                           <h1 style={{fontWeight:'900', fontSize:'8em'}}>-40% </h1>
                           <p style={{fontWeight:'900', fontSize:'2em', margin:'0'}}>sur tous nos produits</p>
                        </div>
