import React, {useState, useEffect} from 'react';
import {useRouteMatch} from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios'

//components
import Navbar from '../components/Navbar'

const Styles = styled.div`
display:flex;
flex-direction:row;
justify-content:flex-start;
align-items:flex-start;
height:100vh;
padding:8%;

.left-side{
    padding-right:10%;
    width:50%;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:flex-end;
}

.right-side{
    padding-left:5%;
    width:50%;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:flex-start;
}
p{
    padding:0;
    margin:0
}
`;

function Detail(){
    let match = useRouteMatch();
    const [pneu, setPneu] = useState([])
    
    useEffect(() => {
        var id = match.params.id_pneu
        axios.post(`${process.env.REACT_APP_API_URL}/pneu/details`, {
            id
        })
        .then(res => {
            console.log(res.data[0])
           setPneu(res.data[0])
        })
        .catch(err => {
            console.log(err)
        })
    }, []);
    return(
        <React.Fragment>
            <Navbar bg="dark" variant="dark"/>
            <Styles>
                <div className='left-side'>
                    <img
                        src={`https://www.monsterstudio.org${pneu.image_url}`}
                        width='280'
                        height='330'
                        className='image'
                    />
                    <img
                        src={`https://www.monsterstudio.org${pneu.marque_img}`}
                        width='150'
                        height='30'
                    />
                </div>
                <div className='right-side'>
                    <p>Pneu {pneu.designation_pneu}</p>
                    <p>Modele</p>
                    <p>{pneu.collection}</p>
                    <p>Dimension</p>
                    <p>{pneu.largeur}/ {pneu.hauteur} R{pneu.diametre}  {pneu.charge} {pneu.vitesse}</p>
                    <p>Gamme</p>
                    <p>competition</p>
                    <p>Usage</p>
                    <p>trackday (semi-slick)</p>
                    <p>Point Fort</p>
                    <p>Design optimise le grip et le drainage</p>
                    <p>Carcasse renforcée dans les flancs</p>
                    <p>Nouveau mélange de gomme</p>
                </div> 
            </Styles>            
        </React.Fragment>
    )
    
}
export default Detail; 

/**
 * 
    {pneu.adherence}
    {pneu.carburant}
    {pneu.bruit}

    {pneu.designation_pneu}
    {pneu.marque}
    {pneu.type}        
 */
